contract INERTIA is ERC20UpgradeSafe, OwnableUpgradeSafe {
    
    using SafeCast for int256;
    using SafeMath for uint256;
    using Address for address;

    mapping (address=>uint256) private _purchasedLast;
    
    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcluded;
    address[] private _excluded;
	
	uint256 private _totalSupply;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private _rTotal;
    uint256 private _tFeeTotal;
    
    uint256 private constant DECIMALS = 9;
    
    uint256[] public _sellFeePeriods;
    uint256[] public _sellFeePcts;
    
    uint256 public _buyFeePct;
    uint256 public _setFeesTimestamp;
    
    uint256 public _minTransferBalancePct;
    
    
    bool public _trWalletToWalletAllowed;
    uint256 public _trLaunchBlocksLocked;
    
    uint256 public _trLaunchMaxBuyAmount;
    uint256 public _trLaunchMaxBuySecs;
    
    uint256 public _trLaunchVestingSecs;
    uint256 public _trLaunchVestingStageSecs;
    uint256 public _trLaunchVestingStages;
    
    uint256 public _trPresaleStageMins;
    uint256 public _trPresaleStages;
        
    
    mapping (address => bool) private _isDistributor;
    mapping (address => bool) private _isExchanger;
    
    uint256 public _launchBlock;
    uint256 public _launchTimestamp;
    
    address[] public _launchLocked;
    
    mapping (address => uint256) public _launchVested;
    
    mapping (address => uint256) public _presale;
    
    uint256 public _initialDistributionTimestamp;
    
    address public _dev;
    
    uint256 _distributedPctTotal;
    uint256 _distributedTimestamp;
    
    
    function initialize(uint256 initialSupply, address dev)
        public
        initializer
    {
        __ERC20_init("inertia.money", "INR");
        _setupDecimals(uint8(DECIMALS));
        __Ownable_init();
        
        _totalSupply = initialSupply;
        _rTotal = (MAX - (MAX % _totalSupply));
        
        //fees
        
        //_sellFeePeriods = [24H, 1H, 30m, 15m, 0];
        _sellFeePeriods = [86400, 3600, 1800, 900, 0];
        _sellFeePcts = [200, 300, 500, 1500, 2900];
        
        _buyFeePct = 50;
        _minTransferBalancePct = 4000; //40%
        
        //transfer rules
        
        _trWalletToWalletAllowed = false;
        _trLaunchBlocksLocked = 2;
        
        _trLaunchMaxBuyAmount = 100000 * 10 ** DECIMALS;
        _trLaunchMaxBuySecs = 10 minutes;
                
        _trLaunchVestingSecs = 10 minutes;
        _trLaunchVestingStageSecs = 10 minutes;
        _trLaunchVestingStages = 10; //10%
        
        
        _trPresaleStageMins = 60;
        _trPresaleStages = 8;
        
        
        _dev = dev;
        
        setDistributor(_msgSender(), true);
        setDistributor(_dev, true);
        
    }
    
    function distributePresale(address[] memory presaleAddresses, uint256[] memory presaleSupplyPcts) public onlyOwner {
        require(_distributedTimestamp == 0, "Already distributed supply");

        for (uint256 i = 0; i < presaleAddresses.length; i++) {
            
            address presaleAddress = presaleAddresses[i];
            uint256 pctT = presaleSupplyPcts[i];
            
            _distribute(presaleAddress, pctT, false);
            _presale[presaleAddress] = balanceOf(presaleAddress);
            
            _distributedPctTotal = _distributedPctTotal.add(pctT);
        }

    }
    
    function distributeCore(uint256 devPct) public onlyOwner {
        require(_distributedTimestamp == 0, "Already distributed supply");
    
        _distribute(_dev, devPct, true);
        _distributedPctTotal = _distributedPctTotal.add(devPct);
        
        //remainder to deployer
        _distribute(_msgSender(), uint256(10**18).sub(_distributedPctTotal), true);
        
        _distributedTimestamp = now;
    }
    
    function _distribute(address account, uint256 pctSupply, bool excluded) internal {
        
        uint256 amountT = _totalSupply.mul(pctSupply).div(10**18);
        uint256 amountR = rFromToken(amountT, false);
        
        _rOwned[account] = amountR;
        if(excluded)
            _tOwned[account] = amountT;
        
        emit Transfer(address(0), account, amountT);
        
    }
    
    function setPresale(address account, uint256 amount) public onlyOwner() {
        _presale[account] = amount;
    }
    
    /**
     * info() - called by frontend
     * 
     * Returns :
     * 
     * - senders balance of INR (9 DECIMALS)
     * - senders last purchase UTC timestamp (seconds) (0 if not purchased)
     * - seconds since last purchase (0 if not purchased)
     * - senders current sell fee percent (2 DECIMALS) eg. 2015 = 20.15%
     * - seconds until next sell fee pct change (will be 0 if sell fee percent unchanged)
     * - senders next sell fee percent (2 DECIMALS) (will be current sell fee pct value if sell fee percent unchanged)
     * 
     */
    
    function info() public view returns (uint256, uint256, uint256, uint256, uint256, uint256) {
        
        uint256 purchaseTimestamp = _purchasedLast[_msgSender()];
        uint256 purchaseSecs = 0;
        
        uint256 sellFeeNowPct = 0;
        
        uint256 sellFeeNextSecs = 0;
        uint256 sellFeeNextPct = 0;
        
        if(purchaseTimestamp == 0 && balanceOf(_msgSender()) > 0 && _presale[_msgSender()] > 0)
            purchaseTimestamp = now - 24 hours;
        
        if(purchaseTimestamp > 0) {
            
            purchaseSecs = now - purchaseTimestamp;
            
            for (uint256 i = 0; i < _sellFeePeriods.length; i++) {
                if(purchaseSecs >= _sellFeePeriods[i]) {
                    
                    sellFeeNowPct = _sellFeePcts[i];
                    
                    sellFeeNextSecs = 0;
                    sellFeeNextPct = sellFeeNowPct;
                    if(i > 0) {
                        sellFeeNextSecs = _sellFeePeriods[i-1].sub(purchaseSecs);
                        sellFeeNextPct = _sellFeePcts[i-1];
                    }
                    break;
                }
            }
            
        }
        
        return (balanceOf(_msgSender()), purchaseTimestamp, purchaseSecs, sellFeeNowPct, sellFeeNextSecs, sellFeeNextPct);
    }
    
    function setDistributor(address account, bool distributor) public onlyOwner() {
        _isDistributor[account] = distributor;
        if(distributor)
            excludeAccount(account);
        else
            includeAccount(account);
    }
    
    function setExchanger(address account, bool exchanger) public onlyOwner() {
        _isExchanger[account] = exchanger;
        if(exchanger)
            excludeAccount(account);
        else
            includeAccount(account);
    }
    
    function setFees(uint256[] memory sellFeePeriods, uint256[] memory sellFeePcts, uint256 buyFeePct, uint256 minTransferBalancePct) external onlyOwner() {
        require(sellFeePeriods.length > 0 && sellFeePeriods.length == sellFeePcts.length, "Invalid sell fees");

        _sellFeePeriods = sellFeePeriods;
        _sellFeePcts = sellFeePcts;
        _buyFeePct = buyFeePct;
        
        _minTransferBalancePct = minTransferBalancePct;
        
        _setFeesTimestamp = now;
    }
    
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromR(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual override returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual override returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function diffract(uint256 tAmount) public {
        address sender = _msgSender();
        require(!_isExcluded[sender], "Excluded addresses cannot call this function");
        (uint256 rAmount,,,,) = _getValues(tAmount, 100);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tFeeTotal = _tFeeTotal.add(tAmount);
    }

    function rFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _totalSupply, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,) = _getValues(tAmount, 100);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,) = _getValues(tAmount, 100);
            return rTransferAmount;
        }
    }

    function tokenFromR(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total supply");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }

    function excludeAccount(address account) public onlyOwner() {
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromR(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeAccount(address account) public onlyOwner() {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }
    
    function launchLock(address account, bool locked) public onlyOwner() {
        _launchLock(account, locked);
    }
    
    function _launchLock(address account, bool locked) internal {
        
        uint256 lockedIndex = MAX;
        
        for (uint256 i = 0; i < _launchLocked.length; i++) {
            if(account == _launchLocked[i]) {
                lockedIndex = i;
                break;
            }
        }
        
        if(locked) {
            if(lockedIndex == MAX)
                _launchLocked.push(account);
        } else {
            if(lockedIndex != MAX) {
                _launchLocked[lockedIndex] = _launchLocked[_launchLocked.length-1];
                _launchLocked.pop();
                
                _purchasedLast[account] = now;
            }
        }
        
    }

    function _approve(address owner, address spender, uint256 amount) internal override {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    
    
    function setTransferRules(
    
        bool trWalletToWalletAllowed,
        uint256 trLaunchBlocksLocked,
        uint256 trLaunchMaxBuyAmount,
        uint256 trLaunchMaxBuySecs,
        uint256 trLaunchVestingSecs,
        uint256 trLaunchVestingStageSecs,
        uint256 trLaunchVestingStages,
        uint256 trPresaleStageMins,
        uint256 trPresaleStages,
        uint256 launchBlock
    
        ) external onlyOwner() {
        
        if(trWalletToWalletAllowed != _trWalletToWalletAllowed) _trWalletToWalletAllowed = trWalletToWalletAllowed;
        
        if(trLaunchBlocksLocked != _trLaunchBlocksLocked) _trLaunchBlocksLocked = trLaunchBlocksLocked;
        
        if(trLaunchMaxBuyAmount != _trLaunchMaxBuyAmount) _trLaunchMaxBuyAmount = trLaunchMaxBuyAmount;
        
        if(trLaunchMaxBuySecs != _trLaunchMaxBuySecs) _trLaunchMaxBuySecs = trLaunchMaxBuySecs;
        
        if(trLaunchVestingSecs != _trLaunchVestingSecs) _trLaunchVestingSecs = trLaunchVestingSecs;
        
        if(trLaunchVestingStageSecs != _trLaunchVestingStageSecs) _trLaunchVestingStageSecs = trLaunchVestingStageSecs;
        
        if(trLaunchVestingStages != _trLaunchVestingStages) _trLaunchVestingStages = trLaunchVestingStages;

        if(trPresaleStageMins != _trPresaleStageMins) _trPresaleStageMins = trPresaleStageMins;
        
        if(trPresaleStages != _trPresaleStages) _trPresaleStages = trPresaleStages;
        
        if(launchBlock != _launchBlock) _launchBlock = launchBlock;
        
    }
    
    function _applyTransferRules(address sender, address recipient, uint256 amount) internal {
        
        bool senderLocked = false;
        
        for (uint256 i = 0; i < _launchLocked.length; i++) {
            if(sender == _launchLocked[i]) {
                senderLocked = true;
                break;
            }
        }
        
        if(_launchBlock == 0) {
            
            require(_isDistributor[sender] || _isDistributor[recipient], "Transfers blocked before launch");
            
            if(_isDistributor[sender] && _isExchanger[recipient]) {
                _launchBlock = block.number;
                _launchTimestamp = now;
            }
            
            return;
        }
        
        if(_isDistributor[sender] || _isDistributor[recipient])
            return;
            
        if(senderLocked || !_trWalletToWalletAllowed)
            require(_isExchanger[sender] || _isExchanger[recipient], "Wallet to Wallet transfers currently locked, please contact admins");
        
        //buying
        if(_isExchanger[sender] && !_isExchanger[recipient]) {
            
            if((now - _launchTimestamp) < _trLaunchMaxBuySecs) {
                require(amount < _trLaunchMaxBuyAmount, "Buy amounts limited at launch, please buy smaller amount");
            }
            
            if((block.number - _launchBlock) < _trLaunchBlocksLocked) {
                _launchLock(recipient, true);
            }
            
            if((now - _launchTimestamp) < _trLaunchVestingSecs) {
                _launchVested[recipient] = _launchVested[recipient].add(amount);
            }
            
        }            

        //selling
        if(!_isExchanger[sender] && _isExchanger[recipient]) {
            
            require(!senderLocked, "Wallet sells currently locked, please contact admins");

            uint256 minBalanceAfter = 0;
            
            uint256 presaleAmount = _presale[sender];
            if(presaleAmount > 0) {
                
                uint256 launchMins = (now - _launchTimestamp).div(60);
                
                uint256 presaleStage = launchMins.div(_trPresaleStageMins);
                
                if(presaleStage < _trPresaleStages) {
                
                    minBalanceAfter = presaleAmount;
                    
                    if(presaleStage >= 1) {
                        minBalanceAfter = minBalanceAfter.sub(presaleAmount.mul(2+presaleStage).div(2+_trPresaleStages));
                    }
                    
                }
                
            }
            
            uint256 vestedAmount = _launchVested[sender];
            
            if(vestedAmount > 0) {
                
                uint256 vestingStage = (now - _launchTimestamp).div(_trLaunchVestingStageSecs).add(1);
                
                if(vestingStage < _trLaunchVestingStages) {
                    minBalanceAfter = minBalanceAfter.add(vestedAmount.mul(_trLaunchVestingStages - vestingStage).div(_trLaunchVestingStages));
                }
                
            }
            
            if(minBalanceAfter > 0)
                require(balanceOf(sender).sub(amount) >= minBalanceAfter, "Sells vested - above maximum allowed amount");   
            
        }   
        
        return;
    }
    
    function calculateSellFeePct(address sender) public view returns (uint256) {
        
        uint256 _seconds = now - _purchasedLast[sender];
            
        for (uint256 i = 0; i < _sellFeePeriods.length; i++) {
            if(_seconds >= _sellFeePeriods[i]) {
                return _sellFeePcts[i];
            }
        }
        
        return 0;
            
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        
        _applyTransferRules(sender, recipient, amount);

        if(_isDistributor[sender] || _isDistributor[recipient]) {
            _transferBothExcluded(sender, recipient, amount, 0);
        } else {
            
            uint256 sellFeePercent = calculateSellFeePct(sender);
            
            bool isMinTransferBalance = (amount >= balanceOf(recipient).mul(_minTransferBalancePct).div(10000));
        
            if (_isExchanger[sender] && !_isExchanger[recipient]) {
                //buying
                
                require(isMinTransferBalance, "Transfer amount must be % greater than current recipient balance");
                _transferFromExcluded(sender, recipient, amount, _buyFeePct);
                
            } else if (!_isExchanger[sender] && _isExchanger[recipient]) {
                //selling
                
                _transferToExcluded(sender, recipient, amount, sellFeePercent);
            } else if (!_isExchanger[sender] && !_isExchanger[recipient]) {
                //wallet to wallet
                
                require(isMinTransferBalance, "Transfer amount must be % greater than current recipient balance");
                _transferStandard(sender, recipient, amount, sellFeePercent);
            } else if (_isExchanger[sender] && _isExchanger[recipient]) {
                //exchange to exchange
                
                _transferBothExcluded(sender, recipient, amount, 0);
            } else {
                require(isMinTransferBalance, "Transfer amount must be % greater than current recipient balance");
                _transferStandard(sender, recipient, amount, sellFeePercent);
            }
            
             _purchasedLast[recipient] = now;
         
        }
    }
    

    function _transferStandard(address sender, address recipient, uint256 tAmount, uint256 tFeePercent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount, tFeePercent);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);       
        _diffractFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferToExcluded(address sender, address recipient, uint256 tAmount, uint256 tFeePercent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount, tFeePercent);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);           
        _diffractFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferFromExcluded(address sender, address recipient, uint256 tAmount, uint256 tFeePercent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount, tFeePercent);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);   
        _diffractFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferBothExcluded(address sender, address recipient, uint256 tAmount, uint256 tFeePercent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount, tFeePercent);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);        
        _diffractFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _diffractFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function _getValues(uint256 tAmount, uint256 tFeePercent) private view returns (uint256, uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tFee) = _getTValues(tAmount, tFeePercent);
        uint256 currentRate =  _getRate();
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee, currentRate);
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee);
    }

    function _getTValues(uint256 tAmount, uint256 tFeePercent) private pure returns (uint256, uint256) {
        uint256 tFee = tAmount.mul(tFeePercent).div(10000);
        uint256 tTransferAmount = tAmount.sub(tFee);
        return (tTransferAmount, tFee);
    }

    function _getRValues(uint256 tAmount, uint256 tFee, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee);
        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _totalSupply;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _totalSupply);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_totalSupply)) return (_rTotal, _totalSupply);
        return (rSupply, tSupply);
    }
}
