        uint256 rate,
        address payable wallet,
        IERC20 token
    ) public {
        require(rate > 0, "Crowdsale: rate is 0");
        require(wallet != address(0), "Crowdsale: wallet is the zero address");
        require(
            address(token) != address(0),
            "Crowdsale: token is the zero address"
        );

        _rate = rate;
        _wallet = wallet;
        _token = token;
