contract EMTI is IERC20, Auth {
    using SafeMath for uint256;

    address WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address DEAD = 0x000000000000000000000000000000000000dEaD;
    address ZERO = 0x0000000000000000000000000000000000000000;

    string constant _name = "Elon Tweet Inu";
    string constant _symbol = "EMTI";
    uint8 constant _decimals = 18;

    uint256 _totalSupply = 10000000000 * (10 ** _decimals); //10 Billion

    mapping (address => uint256) _balances;
    mapping (address => mapping (address => uint256)) _allowances;

    mapping (address => bool) isFeeExempt;

    uint256 liquidityFee    = 4;
    uint256 marketingFee    = 6;
    uint256 public totalFee = 10;
    uint256 feeDenominator  = 100;
    uint256 amountGasProcessing = 30000;

    address public autoLiquidityReceiver;
    address public marketingFeeReceiver;

    uint256 targetLiquidity = 50;
    uint256 targetLiquidityDenominator = 100;
    uint256 _maxTxAmount = _totalSupply * 1 / 1000; // 0.01% of supply;

    IDEXRouter public router;
    IEmpireRouter public empireRouter;
    address public pair;
    address public pair2;

    bool public swapEnabled = false;
    bool public tradingEnabled = false;
    uint256 public swapThreshold = _totalSupply * 1 / 10000; // 0.001% of supply
    bool inSwap;
    modifier swapping() { inSwap = true; _; inSwap = false; }

    constructor () Auth(msg.sender) {
        router = IDEXRouter(0xe7A504316BebbE540496E29798187c9ECAD6ef4F);
        IEmpireRouter _empireRouter = IEmpireRouter(0xe7A504316BebbE540496E29798187c9ECAD6ef4F);

        empireRouter = _empireRouter;

        PairType pairType =
            address(this) < WETH
                ? PairType.SweepableToken1
                : PairType.SweepableToken0;

        pair = IEmpireFactory(router.factory()).createPair(WETH, address(this), pairType, 0);
        pair2 = pair;

        _allowances[address(this)][address(router)] = uint256(-1);

        isFeeExempt[msg.sender] = true;
        isFeeExempt[marketingFeeReceiver] = true;

        autoLiquidityReceiver = DEAD;
  
        marketingFeeReceiver = address(0xBeB0C19044eed4765BC53f55B1343475B6Bf5a10);
        
        _balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }
    receive() external payable { }

    function totalSupply() external view override returns (uint256) { return _totalSupply; }
    function decimals() external pure override returns (uint8) { return _decimals; }
    function symbol() external pure override returns (string memory) { return _symbol; }
    function name() external pure override returns (string memory) { return _name; }
    function getOwner() external view override returns (address) { return owner; }
    function balanceOf(address account) public view override returns (uint256) { return _balances[account]; }
    function allowance(address holder, address spender) external view override returns (uint256) { return _allowances[holder][spender]; }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function approveMax(address spender) external returns (bool) {
        return approve(spender, uint256(-1));
    }

    function transfer(address recipient, uint256 amount) external override returns (bool) {
        return _transferFrom(msg.sender, recipient, amount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
        if(_allowances[sender][msg.sender] != uint256(-1)){
            _allowances[sender][msg.sender] = _allowances[sender][msg.sender].sub(amount, "Insufficient Allowance");
        }

        return _transferFrom(sender, recipient, amount);
    }

    function _transferFrom(address sender, address recipient, uint256 amount) internal returns (bool) {
        if(sender != owner && recipient != owner) {
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");
            require(tradingEnabled, "Contract is paused to prevent malacious activity.");
        }
        
        if(inSwap){ return _basicTransfer(sender, recipient, amount); }

        if(shouldSwapBack()){ swapBack(); }

        _balances[sender] = _balances[sender].sub(amount, "Insufficient Balance");

        uint256 amountReceived = shouldTakeFee(sender) ? takeFee(sender, amount) : amount;
        _balances[recipient] = _balances[recipient].add(amountReceived);

        emit Transfer(sender, recipient, amountReceived);
        return true;
    }

    function _basicTransfer(address sender, address recipient, uint256 amount) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(amount, "Insufficient Balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function shouldTakeFee(address sender) internal view returns (bool) {
        return !isFeeExempt[sender];
    }

    function takeFee(address sender, uint256 amount) internal returns (uint256) {
        uint256 feeAmount = amount.mul(totalFee).div(feeDenominator);

        _balances[address(this)] = _balances[address(this)].add(feeAmount);
        emit Transfer(sender, address(this), feeAmount);

        return amount.sub(feeAmount);
    }

    function shouldSwapBack() internal view returns (bool) {
        return msg.sender != pair && msg.sender != pair2
        && !inSwap
        && swapEnabled
        && _balances[address(this)] >= swapThreshold;
    }

    function recover(uint256 amountPercentage) external onlyOwner {
        uint256 amountETH = address(this).balance;
        payable(marketingFeeReceiver).transfer(amountETH * amountPercentage / 100);
    }

    function recoverTokens(uint256 amountPercentage) external onlyOwner {
        uint256 amountTokens = balanceOf(address(this));
        payable(marketingFeeReceiver).transfer(amountTokens * amountPercentage / 100);
    }

    function swapBack() internal swapping {
        uint256 dynamicLiquidityFee = isOverLiquified(targetLiquidity, targetLiquidityDenominator) ? 0 : liquidityFee;
        uint256 amountToLiquify = balanceOf(address(this)).mul(dynamicLiquidityFee).div(totalFee).div(2);
        uint256 amountToSwap = balanceOf(address(this)).sub(amountToLiquify);

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = WETH;

        uint256 balanceBefore = address(this).balance;

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountToSwap,
            0,
            path,
            address(this),
            block.timestamp
        );

        uint256 amountETH = address(this).balance.sub(balanceBefore);

        uint256 totalETHFee = totalFee.sub(dynamicLiquidityFee.div(2));

        uint256 amountETHLiquidity = amountETH.mul(dynamicLiquidityFee).div(totalETHFee).div(2);
        uint256 amountETHMarketing = amountETH.mul(marketingFee).div(totalETHFee);

        (bool tmpSuccess,) = payable(marketingFeeReceiver).call{value: amountETHMarketing, gas: amountGasProcessing}("");

        tmpSuccess = false;

        if(amountToLiquify > 0){
            router.addLiquidityETH{value: amountETHLiquidity}(
                address(this),
                amountToLiquify,
                0,
                0,
                autoLiquidityReceiver,
                block.timestamp
            );
            emit AutoLiquify(amountETHLiquidity, amountToLiquify);
        }
    }
    
    function setTradingEnabled(address _pair) external authorized {
        if(!tradingEnabled) { 
            tradingEnabled = true; 
            pair2 = _pair;
        }
    }
    
    function setMaxTxAmount(uint256 amount) external authorized {
        require(amount > _totalSupply * 1 / 1000, "tx amount must be more than 0.1%");
        _maxTxAmount = amount;
    }

    function setIsFeeExempt(address holder, bool exempt) external authorized {
        isFeeExempt[holder] = exempt;
    }

    function setFeeReceivers(address _marketingFeeReceiver) external authorized {
        marketingFeeReceiver = _marketingFeeReceiver;
    }
    
    function setAmountGasProcessing(uint256 _amountGasProcessing) external authorized {
        amountGasProcessing = _amountGasProcessing;
    }
    
    function setSwapSettings(bool _enabled) external authorized {
        swapEnabled = _enabled;
    }

    function setSwapBackSettings(bool _enabled, uint256 _amount) external authorized {
        swapEnabled = _enabled;
        swapThreshold = _amount;
    }

    function setTargetLiquidity(uint256 _target, uint256 _denominator) external authorized {
        targetLiquidity = _target;
        targetLiquidityDenominator = _denominator;
    }

    function getCirculatingSupply() public view returns (uint256) {
        return _totalSupply.sub(balanceOf(DEAD)).sub(balanceOf(ZERO));
    }

    function getLiquidityBacking(uint256 accuracy) public view returns (uint256) {
        return accuracy.mul(balanceOf(pair).mul(2)).div(getCirculatingSupply());
    }

    function isOverLiquified(uint256 target, uint256 accuracy) public view returns (bool) {
        return getLiquidityBacking(accuracy) > target;
    }

    event AutoLiquify(uint256 amountETH, uint256 amountTo);

    //SWEEPABLE CONFIG
    modifier onlyPair() {
        require(
            msg.sender == pair,
            "Empire::onlyPair: Insufficient Privileges"
        );
        _;
    }

    function getMaxSweepable() public view returns(uint256) {
        return IEmpirePair(pair).getMaxSweepable();
    }
    
    function sweep(uint256 amount, bytes calldata data) external onlyOwner() {
        IEmpirePair(pair).sweep(amount, data);
    }

    function empireSweepCall(uint256 amount, bytes calldata) external onlyPair() {
        IERC20(empireRouter.WETH()).transfer(marketingFeeReceiver, amount);
    }

    function unsweep(uint256 amount) external onlyOwner() {
        IERC20(empireRouter.WETH()).approve(pair, amount);
        IEmpirePair(pair).unsweep(amount);
    }
    
    function updateLP(address _pair, address _pair2, address _router) external onlyOwner() {
        pair = _pair;
        pair2 = _pair2;
        router = IDEXRouter(_router);
    }

}
