contract BonesYield is Ownable, ReentrancyGuard, IERC721Receiver {
    using EnumerableSet for EnumerableSet.UintSet;

    struct ContractSettings {
        bool isLocked;
        uint256 baseRate;
        uint256 start;
        uint256 end;
    }

    event RewardPaid(address indexed user, uint256 reward);

    address public stakingContractAddress;
    address public ERC20ContractAddress;
    uint256 stakingExpirationDate;

    uint256 constant CARDS_BOOSTER = 2;
    uint256 constant CARDS_REQUIRED_FOR_BOOST = 3;


    mapping(address => ContractSettings) public contractSettings;
    mapping(address => bool) public trustedContracts;
    mapping(bytes32 => uint256) public lastClaim;


    // mappings
    mapping(address => EnumerableSet.UintSet) private _deposits;

    // Prevents new contracts from being added or changes to disbursement if permanently locked
    bool public isClaimActive = false;
    bool public isStakingActive = false;

    constructor(address _stakingContractAddress, address _ERC20ContractAddress) {
        stakingContractAddress = _stakingContractAddress;
        ERC20ContractAddress = _ERC20ContractAddress;
    }

    function addContractConfig(
        address _address,
        uint256 _baseRate,
        uint256 _end
    ) external onlyOwner {
        trustedContracts[_address] = true;
        contractSettings[_address] = ContractSettings({
            isLocked: true,
            baseRate: _baseRate,
            start: block.timestamp,
            end: _end
        });
    }


    function setStakingContractAddress(address _contractAddress) external onlyOwner {
        stakingContractAddress = _contractAddress;
    }

    function setERC20ContractAddress(address _contractAddress) external onlyOwner {
        ERC20ContractAddress = _contractAddress;
    }

    function toggleClaim() external onlyOwner {
        isClaimActive = !isClaimActive;
    }

    function toggleStaking() external onlyOwner {
        isStakingActive = !isStakingActive;
    }

    function startYielding() external onlyOwner {
        isStakingActive = true;
        isClaimActive = true;
    }

    function toggleContractYielding(address _contractAddress) public onlyOwner {
        require(trustedContracts[_contractAddress], "Contract is not trusted");
        contractSettings[_contractAddress].isLocked = !contractSettings[_contractAddress].isLocked;
    }

    /**
        - sets an end date for when rewards officially end
     */
    function setEndDateForContract(address _contractAddress, uint256 _endTime) public onlyOwner {
        require(trustedContracts[_contractAddress], "The contract is not trusted");
        contractSettings[_contractAddress].end = _endTime;
    }


    function setRateForContract(address _contractAddress, uint256 _rate) public onlyOwner {
        require(trustedContracts[_contractAddress], "The contract is not trusted");
        contractSettings[_contractAddress].baseRate = _rate;
    }

    function claimRewards(address _contractAddress, uint256[] calldata _tokenIds) public returns (uint256) {
        require(isClaimActive, "Claim is active");
        require(trustedContracts[_contractAddress], "The contract address is not trusted");
        require(contractSettings[_contractAddress].end > block.timestamp, "Time for claiming has expired");
        uint256 totalUnclaimedRewards = 0;

        for(uint256 i = 0; i < _tokenIds.length; i++) {
            uint256 _tokenId = _tokenIds[i];

            require(IProducer(_contractAddress).ownerOf(_tokenId) == msg.sender, "Caller does not own the token being claimed for.");

            uint256 unclaimedReward = computeUnclaimedReward(_contractAddress, _tokenId);
            totalUnclaimedRewards += unclaimedReward;

            bytes32 lastClaimKey = keccak256(abi.encode(_contractAddress, _tokenId));
            lastClaim[lastClaimKey] = block.timestamp;
        }

        IERC20(ERC20ContractAddress).transfer(msg.sender, totalUnclaimedRewards);

        emit RewardPaid(msg.sender, totalUnclaimedRewards);

        return totalUnclaimedRewards;
    }

    function getUnclaimedRewardsAmount(address contractAddress, uint256[] calldata _tokenIds) public view returns (uint256) {

        uint256 totalUnclaimedRewards = 0;

        for(uint256 i = 0; i < _tokenIds.length; i++) {
            totalUnclaimedRewards += computeUnclaimedReward(contractAddress, _tokenIds[i]);
        }

        return totalUnclaimedRewards;
    }


    function computeUnclaimedReward(address _contractAddress, uint256 _tokenId) internal view returns (uint256) {
        bytes32 lastClaimKey = keccak256(abi.encode(_contractAddress, _tokenId));
        uint256 lastClaimDate = lastClaim[lastClaimKey];
        uint256 baseRate = contractSettings[_contractAddress].baseRate;

        // if there has been a lastClaim, compute the value since lastClaim
        if (lastClaimDate != uint256(0)) {

            return computeAccumulatedReward(lastClaimDate, baseRate);
        } else {
            uint256 totalReward = computeAccumulatedReward(contractSettings[_contractAddress].start, baseRate);

            return totalReward;
        }
    }

    function getLastClaimedTime(address _contractAddress, uint256 _tokenId) public view returns (uint256) {
        require(trustedContracts[_contractAddress], "The contract address is not trusted");

        bytes32 lastClaimKey = keccak256(abi.encode(_contractAddress, _tokenId));

        return lastClaim[lastClaimKey];
    }

    function computeAccumulatedReward(uint256 _lastClaimDate, uint256 _baseRate) internal view returns (uint256) {
        require(block.timestamp > _lastClaimDate, "Last claim date must be smaller than block timestamp");

        uint256 secondsElapsed = block.timestamp - _lastClaimDate;
        uint256 accumulatedReward = secondsElapsed * _baseRate / 1 days;

        uint256 multiplier = getMultiplier(msg.sender);

        return accumulatedReward * multiplier;
    }

    // STAKING

    function setExpiration(uint256 _expiration) public onlyOwner {
        require(!isStakingActive, "Staking is not active");
        stakingExpirationDate = _expiration;
    }

    //check deposit amount.
    function depositsOf(address _account) external view returns (uint256[] memory) {
        require(isStakingActive, "Staking is not active");

        EnumerableSet.UintSet storage depositSet = _deposits[_account];
        uint256[] memory tokenIds = new uint256[] (depositSet.length());

        for (uint256 i; i < depositSet.length(); i++) {
            tokenIds[i] = depositSet.at(i);
        }

        return tokenIds;
    }

    function countDepositsOf(address _account) public view returns (uint256) {
        EnumerableSet.UintSet storage depositSet = _deposits[_account];

        return depositSet.length();
    }

    function getMultiplier(address _account) internal view returns (uint256) {
        if (countDepositsOf(_account) >= CARDS_REQUIRED_FOR_BOOST) {
            return CARDS_BOOSTER;
        }

        return 1;
    }

    //deposit function.
    function deposit(uint256[] calldata _tokenIds) external {
        require(isStakingActive, "Staking is not active");

        for (uint256 i; i < _tokenIds.length; i++) {
            IERC721(stakingContractAddress).safeTransferFrom(
                msg.sender,
                address(this),
                _tokenIds[i],
                ""
            );

            _deposits[msg.sender].add(_tokenIds[i]);
        }
    }

    //withdrawal function.
    function withdraw(uint256[] calldata _tokenIds) external nonReentrant() {
        require(isStakingActive, "Staking is not active");

        for (uint256 i; i < _tokenIds.length; i++) {
            require(
                _deposits[msg.sender].contains(_tokenIds[i]),
                "Token not deposited or not owned"
            );

            _deposits[msg.sender].remove(_tokenIds[i]);

            IERC721(stakingContractAddress).safeTransferFrom(
                address(this),
                msg.sender,
                _tokenIds[i],
                ""
            );
        }
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}
