// SPDX-License-Identifier: MIT
contract StrategyFortube {
  using SafeERC20 for IERC20;
  using Address for address;
  using SafeMath for uint256;

  address public constant eth_address = address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE);
  address public constant want = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2); //eth
  address public constant output = address(0x1FCdcE58959f536621d76f5b7FfB955baa5A672F); //for
  address public constant unirouter = address(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
  address public constant weth = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2); // used for for <> weth <> usdc route

  address public constant fortube = address(0xdE7B3b2Fe0E7b4925107615A5b199a4EB40D9ca9); //Master contract.
  address public constant fortube_reward = address(0xF8Df2E6E46AC00Cdf3616C4E35278b7704289d82); //Receiving contract

  address public constant usdt = address(0xdAC17F958D2ee523a2206206994597C13D831ec7);

  uint256 public performanceFee = 5000;
  uint256 public constant performanceMax = 10000;

  uint256 public withdrawalFee = 0;
  uint256 public constant withdrawalMax = 10000;

  address public governance;
  address public controller;
  address public burnAddress;

  address[] public swap2YFIIRouting;
  address[] public swap2TokenRouting;

  constructor(address controller_, address burnAddress_) public {
    governance = msg.sender;
    controller = controller_; 
    burnAddress = burnAddress_;
    swap2TokenRouting = [output, usdt, weth]; //for->weth
    doApprove();
  }

  function doApprove() public {
    IERC20(output).safeApprove(unirouter, 0);
    IERC20(output).safeApprove(unirouter, uint256(-1));
  }

  function deposit() public {
    uint256 _want = IERC20(want).balanceOf(address(this));
    if (_want > 0) {
      IWETH(address(weth)).withdraw(_want); //weth->eth
      IFor(fortube).deposit{value: _want}(eth_address, _want);
    }
  }

  // Controller only function for creating additional rewards from dust
  function withdraw(IERC20 _asset) external returns (uint256 balance) {
    require(msg.sender == controller, "!controller");
    require(want != address(_asset), "want");
    balance = _asset.balanceOf(address(this));
    _asset.safeTransfer(controller, balance);
  }

  // Withdraw partial funds, normally used with a vault withdrawal
  function withdraw(uint256 _amount) external {
    require(msg.sender == controller, "!controller");
    uint256 _balance = IERC20(want).balanceOf(address(this));
    if (_balance < _amount) {
      _amount = _withdrawSome(_amount.sub(_balance));
      _amount = _amount.add(_balance);
    }

    uint256 _fee = 0;
    if (withdrawalFee > 0) {
      _fee = _amount.mul(withdrawalFee).div(withdrawalMax);
      IERC20(want).safeTransfer(IController(controller).rewards(), _fee);
    }

    address _vault = IController(controller).vaults(address(want));
    require(_vault != address(0), "!vault"); // additional protection so we don't burn the funds
    IERC20(want).safeTransfer(_vault, _amount.sub(_fee));
  }

  // Withdraw all funds, normally used when migrating strategies
  function withdrawAll() external returns (uint256 balance) {
    require(msg.sender == controller || msg.sender == governance, "!governance");
    _withdrawAll();

    balance = IERC20(want).balanceOf(address(this));

    address _vault = IController(controller).vaults(address(want));
    require(_vault != address(0), "!vault"); // additional protection so we don't burn the funds
    IERC20(want).safeTransfer(_vault, balance);
  }

  function _withdrawAll() internal {
    address _controller = IFor(fortube).controller();
    IFToken fToken = IFToken(IBankController(_controller).getFTokeAddress(eth_address));
    uint256 b = fToken.calcBalanceOfUnderlying(address(this));
    _withdrawSome(b);
  }

  function harvest() public {
    require(!Address.isContract(msg.sender), "!contract");
    IForReward(fortube_reward).claimReward();
    doswap();
    dosplit();
    deposit();
  }

  function doswap() internal {
    uint256 _2token = IERC20(output).balanceOf(address(this));
    if (_2token > 0) {
      UniswapRouter(unirouter).swapExactTokensForTokens(_2token, 0, swap2TokenRouting, address(this), now.add(1800));
    }
  }

  function dosplit() internal {
    uint256 _want = IERC20(want).balanceOf(address(this));
    if (_want > 0) {
      uint256 _fee = _want.mul(performanceFee).div(performanceMax);
      IERC20(want).safeTransfer(IController(controller).rewards(), _fee);
    }
  }

  function _withdrawSome(uint256 _amount) internal returns (uint256) {
    IFor(fortube).withdrawUnderlying(eth_address, _amount);
    IWETH(address(weth)).deposit{value: address(this).balance}();
    return _amount;
  }

  function balanceOfWant() public view returns (uint256) {
    return IERC20(want).balanceOf(address(this));
  }

  function balanceOfPool() public view returns (uint256) {
    address _controller = IFor(fortube).controller();
    IFToken fToken = IFToken(IBankController(_controller).getFTokeAddress(eth_address));
    return fToken.calcBalanceOfUnderlying(address(this));
  }

  function balanceOf() public view returns (uint256) {
    return balanceOfWant().add(balanceOfPool());
  }

  function setGovernance(address _governance) external {
    require(msg.sender == governance, "!governance");
    governance = _governance;
  }

  function setController(address _controller) external {
    require(msg.sender == governance, "!governance");
    controller = _controller;
  }

  function setBurnAddress(address _burnAddress) public {
    require(msg.sender == governance, "!governance");
    burnAddress = _burnAddress;
  }

  function setWithdrawalFee(uint256 _withdrawalFee) external {
    require(msg.sender == governance, "!governance");
    require(_withdrawalFee <= 100, "fee >= 1%"); //max:1%
    withdrawalFee = _withdrawalFee;
  }

  function setSwap2Token(address[] memory _path) public {
    require(msg.sender == governance, "!governance");
    swap2TokenRouting = _path;
  }

  function setPerformanceFee(uint256 _performanceFee) external {
    require(msg.sender == governance, "!governance");
    performanceFee = _performanceFee;
  }

  receive() external payable {}
}
