    {
        require(
            _stakingRewardsGenesis >= block.timestamp,
            "SatellitePoolFactory::constructor: genesis too soon"
        );

        rewardsToken = _rewardsToken;
        stakingRewardsGenesis = _stakingRewardsGenesis;
    }


