// File: contracts/vaults/sashimiLending/SashimiLendingVaultProvider.sol

pragma solidity 0.6.12;







contract SashimiLendingVaultProvider is VaultProvider {

    address public WETH;
    address public SASHIMI;
    address public router;
    address public sashimiRouter;
    address public comptroller;
    SashimiLendingLens public sashimiLendingLens;
    uint256 public balance = 0;

    constructor(address _token, address _profit, address _lend, address _controller, address _WETH, 
        address _SASHIMI,address _router,address _sashimiRouter,address _comptroller, SashimiLendingLens _sashimiLendingLens) public{
        token = _token;
        profit = _profit;
        lend = _lend;
        controller = _controller;
        router = _router;
        sashimiRouter = _sashimiRouter;
        comptroller = _comptroller;
        WETH = _WETH;
        SASHIMI = _SASHIMI;
        sashimiLendingLens = _sashimiLendingLens;
    }

    function deposit(uint256 _amount) override onlyController external {
        depositInternal(_amount);
    }

    function withdraw(uint256 _amount) override onlyController external {
        withdrawInternal(_amount);
    }

    function depositAll() override onlyController external {
        uint256 amount = IERC20(token).balanceOf(msg.sender);
        if (amount > 0)
        {
            depositInternal(amount);
        }
    }

    function withdrawAll() override onlyController external returns (uint256 amount){
        if(balance == 0) return amount;
        amount = IERC20(token).balanceOf(controller);
        withdrawInternal(balance);
        amount = IERC20(token).balanceOf(controller).sub(amount);
    }

    function harvest() override onlyController external returns (uint256 amount){
        uint256 slTokenBalance = SLToken(lend).balanceOf(address(this));
        if(slTokenBalance == 0) return amount;
        if(SLToken(lend).balanceOfUnderlying(address(this)) >= balance){
            if(SLToken(lend).redeem(slTokenBalance) == 0) mint(balance);
        }
        swapSashimiToProfit();
        
        if(profit == WETH) depositETH();
        amount = IERC20(profit).balanceOf(address(this));
        if (amount > 0) IERC20(profit).safeTransfer(controller, amount);
    }

    function earned() override view external returns(uint256){
        (, uint256 slTokenBalance,, uint256 exchangeRateMantissa) = SLToken(lend).getAccountSnapshot(address(this));
        uint256 tokenAmount = slTokenBalance.mul(exchangeRateMantissa).div(1e18);
        uint256 amount = _getAmountOut(SASHIMI,profit,IERC20(SASHIMI).balanceOf(address(this)));
        uint256 tokenEarned = 0;
        if(tokenAmount > balance) tokenEarned = tokenAmount.sub(balance);
        return tokenEarned.add(amount);
    }

    function earnedCurrent() external returns (uint256, uint256){
        uint256 tokenAmount = SLToken(lend).balanceOfUnderlying(address(this));
        uint256 tokenEarned = 0;
        if(tokenAmount > balance) tokenEarned = tokenAmount.sub(balance);
        (,,,uint256 sashimiAccrued) = sashimiLendingLens.getSashimiBalanceMetadataExt(SASHIMI, comptroller, address(this));
        return (tokenEarned, sashimiAccrued);
    }

    receive () external payable{}

    function depositETH() internal{
        uint256 amount = address(this).balance;
        if(amount == 0) return;
        IWETH(WETH).deposit{value:amount}();
    }

    function depositInternal(uint256 _amount) internal {
        IERC20(token).safeTransferFrom(controller, address(this), _amount);     
        if(token == WETH) IWETH(token).withdraw(_amount);
        mint(_amount);
        balance = balance.add(_amount);
    }

    function mint(uint256 _amount) internal {
        if(_amount == 0) return;
        if(token == WETH){
            SLEther(lend).mint{value:_amount}();
        }
        else{
            IERC20(token).safeApprove(lend, _amount);
            require(SLToken(lend).mint(_amount) == 0, "Mint failed");
        }
    }

    function withdrawInternal(uint256 _amount) internal {
        require(SLToken(lend).redeemUnderlying(_amount) == 0, "Redeem failed");
        if(token == WETH){
            IWETH(token).deposit{value:_amount}();
        }
        IERC20(token).safeTransfer(controller, _amount);
        balance = balance.sub(_amount);
    }

    function _getAmountOut(
        address _from,
        address _to,
        uint256 _amount
    ) view internal returns (uint256){
        if(_from == _to || _amount == 0) return _amount;
        address[] memory path;

        if (_from == WETH || _to == WETH) {
            path = new address[](2);
            path[0] = _from;
            path[1] = _to;
        } else {
            path = new address[](3);
            path[0] = _from;
            path[1] = WETH;
            path[2] = _to;
        }
        uint[] memory amounts = IUniswapV2Router02(router).getAmountsOut(_amount, path);
        return amounts[amounts.length - 1];
    }

    function swapSashimiToProfit() internal {
        if(SASHIMI == profit) return;
        uint256 sashimiBalance = IERC20(SASHIMI).balanceOf(address(this));
        if(sashimiBalance == 0) return;
        IERC20(SASHIMI).safeApprove(sashimiRouter, 0);
        IERC20(SASHIMI).safeApprove(sashimiRouter, sashimiBalance);

        address[] memory path = new address[](2);
        path[0] = SASHIMI;
        path[1] = WETH;

        uint256[] memory amounts = IUniswapV2Router02(sashimiRouter).swapExactTokensForTokens(
            sashimiBalance,
            0,
            path,
            address(this),
            now.add(60)
        );
        if(profit == WETH) return;

        address[] memory path2 = new address[](2);
        path2[0] = WETH;
        path2[1] = profit;

        IERC20(WETH).safeApprove(router, 0);
        IERC20(WETH).safeApprove(router, amounts[1]);
        IUniswapV2Router02(router).swapExactTokensForTokens(
            amounts[1],
            0,
            path2,
            address(this),
            now.add(60)
        );
    }
}
