            */
            priceFeed = AggregatorV3Interface(0x8A753747A1Fa494EC906cE90E9f37563A8AF630e);
        }

    //view function to obtain price from chainlink
    function getThePrice() public view returns (int) {
        (
            uint80 roundID, 
            int price,
            uint startedAt,
            uint timeStamp,
            uint80 answeredInRound
        ) = priceFeed.latestRoundData();
        return price;
    }
    
    function f_set_eth_price_fallback(uint256 eth_usd_price) public {
        require(msg.sender == addr_owner);
        p_eth_usd = eth_usd_price;
    }
    
    function f_get_eth_price_fallback() public view returns (uint256) {
        return p_eth_usd;
    }
    
    function f_set_rate(uint256 newRate) public {
        require(msg.sender == addr_owner);
        p_rate = newRate;
    }

    function f_view_rate() public view returns (uint256) {
        return p_rate;
    }

    function _getTokenAmount(uint256 weiAmount) internal view override returns (uint256) {
        
        //add logic here if needed
        return p_rate.mul(weiAmount);
    }
}

