// File: BindBox/BindBox.sol


pragma solidity ^0.8.9;







contract BindBox is OwnableUpgradeable,ReentrancyGuard {
    address public param;
    bytes32 public nonce;
    mapping(address=>bool) public newUserReward;
    
    using Address for address;
    
    event BindBoxOpen(address indexed from,address to,IERC20 token,IERC20 rewardToken,uint amount,uint rewardAmount,uint level);
    
    modifier nonContract() {
        require(!msg.sender.isContract(),'msg sender cannot be contract');
        _;
    }
    
    function init(address _param) public initializer {
        __Ownable_init();
        param=_param;
    }

    function newUserBindBox(address to) external virtual nonReentrant nonContract {
        require(!newUserReward[msg.sender],'newUserBindBox only once');
        // tranfer fee
        (IERC20 token,uint amount)=IBindBoxParam(param).newUserToken();
        require(token!=IERC20(address(0)),'func not open');
        NewCowLib.transferFrom(token,msg.sender,address(this),amount);
        // reward
        (uint switchProbabilityX128,bytes32 _nonce)=NewCowLib.random(keccak256(abi.encode(token,param,nonce)));
        (uint rewardProbabilityX128,bytes32 _nonces)=NewCowLib.random(keccak256(abi.encode(token,param,_nonce)));
        nonce=_nonces;
        (IERC20 rewardToken,uint rewardAmount)=IBindBoxParam(param).newUserRewardToken(switchProbabilityX128,rewardProbabilityX128);
        NewCowLib.transferFrom(rewardToken,address(this),to,rewardAmount);

        newUserReward[msg.sender]=true;
        emit BindBoxOpen(msg.sender,to,token,rewardToken,amount,rewardAmount,1);
    }
    
    function bindBox(IERC20 token,uint amount,address to,uint level) external virtual nonReentrant nonContract {
        require(amount>0,'amount must more than 0');
        NewCowLib.transferFrom(token,msg.sender,address(this),amount);
        (uint randomX128,bytes32 _nonce)=NewCowLib.random(keccak256(abi.encode(token,param,nonce)));
        nonce=_nonce;
        uint award=IBindBoxParam(param).reward(amount,randomX128,level);
        NewCowLib.transferFrom(token,address(this),to,award);

        emit BindBoxOpen(msg.sender,to,token,token,amount,award,level);
    }
    
    function testBindBox(uint amount,uint level) external virtual view returns(uint award) {
        (uint randomX128,)=NewCowLib.random(keccak256(abi.encode(param)));
        award=IBindBoxParam(param).reward(amount,randomX128,level);
    }
    
    function withdraw(IERC20 token,address to,uint amount) external virtual onlyOwner {
        NewCowLib.transferFrom(token,address(this),to,amount);
    }
    
    function changeParam(address _param) external virtual onlyOwner {
        param=_param;
    }
}
