pragma solidity ^0.6.0;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/IERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";

// External contracts
interface ICurveFi {
    function exchange_underlying(int128 i, int128 j, uint256 dx, uint256 min_dy) external;
    function get_dy_underlying(int128 i, int128 j, uint256 dx) external returns (uint256 out);
    function underlying_coins(int128 arg0) external returns (address out);
}


// Main contract
contract CurveSwapArb {
    using SafeMath for uint256;

    enum Pools { Comp, Y, B, S, Tri }

    address payable owner;

    constructor() public {
        owner = msg.sender;
    }

    fallback() external payable {}

    function withdraw(address token) external {
        require(msg.sender == owner, "NOT OWNER");
        IERC20(token).transfer(owner, IERC20(token).balanceOf(address(this)));
    }

    function getPoolAddress(Pools pool) internal pure returns (address poolAddress) {
        if(pool == Pools.Comp){
            return address(0xA2B47E3D5c44877cca798226B7B8118F9BFb7A56);
        }
        if(pool == Pools.Y){
            return address(0x45F783CCE6B7FF23B2ab2D70e416cdb7D6055f51);
        }
        if(pool == Pools.B){
            return address(0x79a8C46DeA5aDa233ABaFFD40F3A0A2B1e5A4F27);
        }
        if(pool == Pools.S){
            return address(0xA5407eAE9Ba41422680e2e00537571bcC53efBfD);
        }
        if(pool == Pools.Tri){
            return address(0xbEbc44782C7dB0a1A60Cb6fe97d0b483032FF1C7);
        }
    }

    function execute(Pools poolIn, Pools poolOut, int128 i, int128 j, uint256 dx) external {
        require(msg.sender == owner, "NOT OWNER");

        ICurveFi curveIn = ICurveFi(getPoolAddress(poolIn));
        ICurveFi curveOut = ICurveFi(getPoolAddress(poolOut));

        // Check if profit
        uint256 dy = curveOut.get_dy_underlying(j, i, curveIn.get_dy_underlying(i, j, dx));
        //require(dy > dx, "NO PROFIT");

        // Execute arbitrage
        // In
        IERC20(curveIn.underlying_coins(i)).approve(getPoolAddress(poolIn), dx);
        curveIn.exchange_underlying(i, j, dx, 0);

        // Out
        uint256 dx2 = IERC20(curveOut.underlying_coins(j)).balanceOf(address(this));
        IERC20(curveOut.underlying_coins(j)).approve(getPoolAddress(poolOut), dx2);
        curveOut.exchange_underlying(j, i, dx2, 0);
    }
}

