contract BasedFishMafiaWire is ERC1155Pausable, ERC1155Holder, VRFConsumerBase {

    event RandomId(uint id);

    mapping(address => bool) private m_admin_map;
    uint private m_salt;

    Contract private constant m_bfm = Contract(0x138Ff21a21DFC06FbfCcf15F2D9Fd290a660E152);
    Contract private constant m_zfm = Contract(0xd66247EdA32bAcC0C06DACC80D19281535CE9D35);

    uint private m_seed;

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, ERC1155Receiver)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function _processAddAdmin(address user) internal {
        m_admin_map[user] = true;
    }

    modifier onlyAdmin() {
        require(m_admin_map[msg.sender], "must be admin to perform this action");
        _;
    }

    function addAdmin(address user) external onlyAdmin {
        _processAddAdmin(user);
    }

    constructor() ERC1155("https://basedfishmafia.com/api/wires/wires.json")
        VRFConsumerBase(
            0xf0d54349aDdcf704F77AE15b96510dEA15cb7952, // VRF
            0x514910771AF9Ca656af840dff83E8264EcF986CA  // LINK
        )
    {
        _processAddAdmin(msg.sender);
    }


    function burn(address user, uint count) external onlyAdmin {
        _burn(user, 0, count);
    }

    function seed() external onlyAdmin returns (bytes32 requestId) {
        uint fee = 2 * 10 ** 18;
        bytes32 keyh = 0xAA77729D3466CA35AE8D28B3BBAC7CC36A5031EFDC430821C02BC31A238AF445;
        require(LINK.balanceOf(address(this)) >= fee, "not enough LINK for transaction");
        return requestRandomness(keyh, fee);
    }

    function fulfillRandomness(bytes32, uint256 randomness) internal override {
        m_seed = randomness;
    }

    function distribute(uint count) external onlyAdmin {
        require(m_seed != 0, "need to seed() random value first");

        uint bfm_supply = m_bfm.totalSupply();
        uint zfm_supply = m_zfm.totalSupply();
        uint combined_supply = bfm_supply + zfm_supply;

        uint numPicked = 0;
        Contract selContract;
        uint randomId;
        address selUser;
        while (numPicked < count) {
            randomId = uint(keccak256(abi.encode(m_seed, m_salt++))) % combined_supply;
            emit RandomId(randomId);

            if (randomId < bfm_supply) {
                selContract = m_bfm;
            } else {
                selContract = m_zfm;
                randomId -= bfm_supply;
            }

            selUser = selContract.ownerOf(randomId);
            if (selUser == address(0xE052113bd7D7700d623414a0a4585BCaE754E9d5) ||
                selUser == address(0xCB6eA8Bb9cDF31c1cAcFD207906520aDc249a856)
            ) {
                continue;
            }

            _mint(selUser, 0, 1, "");
            ++numPicked;
        }
    }
