enum DexPlatform {
    None, //0
    UniV1_Main, //1
    UniV2_Main, //2
    UniV3_Main, //3
    SushiV2_Main, //4
    PancakeV1_Bsc, //5
    PancakeV2_Bsc //6
}

struct DexPair {
    DexPlatform platform;
    address pair;
}

struct FlashParams {
    address payable to;
    DexPair pair0;
    DexPair[] pairs;
    address tokenOut;
    uint amountIn;
    uint amountOut;
}

struct PairInfo {
    uint reserve0;
    uint reserve1;
    uint timestamp;
}

interface IWETH9 is IERC20 {
    function deposit() external payable;
    function withdraw(uint256) external;
}

contract HotToken {
    //contract owner
    address public owner;
    //allowed callers
    mapping(address => bool) public allowed;

    //weth9 address
    IWETH9 public weth9;
    //profit
    uint public profitAmount;

    constructor(address _weth9) {
        owner = payable(msg.sender);
        weth9 = IWETH9(_weth9);
        allowed[owner] = true;
    }

    //for ether receiving
    fallback() payable external {}
    receive() payable external {}

    //allowed for owner
    modifier onlyOwner() {
        require(msg.sender == owner, "no access");
        _;
    }

    //allowed for caller
    modifier onlyAllowed() {
        require(allowed[tx.origin], "no access");
        _;
    }
    /////////////////////////////////////////////////////////////////////////////////
    /////////////////////////////////////////////////////////////////////////////////
    //MUST CALLED FROM OWNER

    //change owner
    function setOwner(address newOwner) public onlyOwner payable {
        require(newOwner != address(0), "invalid address");
    }

    //set allowed
    function setAllowed(address caller, bool permit) public onlyOwner payable {
        allowed[caller] = permit;
    }

    //withdraw
    function withdraw(address token, uint amount, address payable to) public payable onlyOwner {
        if (to == address(0)) {
            to = msg.sender;
        }

        if (token == address(0)) {
            token = address(weth9);
        }

        transferToken(to, token, amount);
    }

    //////////////////////////////////////////////////////////////////////////////////////
    //////////////////////////////////////////////////////////////////////////////////////

    function getPairInfo(bytes calldata data) public view returns (PairInfo[] memory infos){
        DexPair[] memory pairs = toDexPairs(data);
        uint count = pairs.length;
        infos = new PairInfo[](count);
        for (uint i=0; i<count; i++) {
            address pair = pairs[i].pair;
            DexPlatform platform = pairs[i].platform;

            PairInfo memory info;
            if (platform == DexPlatform.UniV3_Main)
            {
                info.reserve0 = IERC20(IUniswapV3Pool(pair).token0()).balanceOf(pair);
                info.reserve1 = IERC20(IUniswapV3Pool(pair).token1()).balanceOf(pair);
            }
            else
            {
                (info.reserve0, info.reserve1, info.timestamp) = IUniswapV2Pair(pair).getReserves();
            }

            infos[i] = info;
        }

        return infos;
    }

    function swapFromEth(bytes calldata path, address tokenIn, uint minProfit) public payable onlyAllowed returns(uint) {
        DexPair[] memory pairs = toDexPairs(path);

        uint amountBefore = msg.value;
        uint amountIn = msg.value;

        for (uint i = 0; i < pairs.length; i++) {
            (address token0, address token1, uint24 fee) = getPairDetails(pairs[i].platform, pairs[i].pair);
            (token0, token1) = tokenIn == token0 ? (token0, token1) : (token1, token0);
            amountIn = swapToken(pairs[i].platform, token0, token1, fee, amountIn);
            tokenIn = token1;
        }

        require(minProfit == 0 || (amountIn > amountBefore && amountIn - amountBefore >= minProfit), "bad task");

        transferToken(msg.sender, tokenIn, 0);

        return amountIn > amountBefore ? amountIn - amountBefore : 0;
    }

