    function swapToken(DexPlatform platform, address tokenIn, address tokenOut, uint24 fee, uint amountIn) internal returns (uint) {
        bool fromEth = tokenIn == address(weth9);
        bool toEth = tokenOut == address(weth9);

        require(!(fromEth && toEth), "only weth");

        address router = getRouter(platform);

        uint amountBefore = tokenOut == address(weth9) ? address(this).balance : IERC20(tokenOut).balanceOf(address(this));

        if (!fromEth) {
            IERC20(tokenIn).approve(router, type(uint).max);
        }

        uint deadline = block.timestamp + 1;

        if (platform == DexPlatform.UniV3_Main) {
            ISwapRouter(router).exactInputSingle{value : fromEth ? amountIn : 0}(ISwapRouter.ExactInputSingleParams({
                tokenIn : tokenIn,
                tokenOut : tokenOut,
                fee : fee,
                recipient : address(this),
                deadline : deadline,
                amountIn : amountIn,
                amountOutMinimum : 1,
                sqrtPriceLimitX96 : 0
            }));

            if (toEth) {
                weth9.withdraw(weth9.balanceOf(address(this)));
            }
        } else {
            address[] memory path = new address[](2);
            path[0] = tokenIn;
            path[1] = tokenOut;

            if (fromEth) {
                IUniswapV2Router02(router).swapExactETHForTokensSupportingFeeOnTransferTokens{value : amountIn}(
                    1,
                    path,
                    address(this),
                    deadline
                );
            } else if(toEth) {
                IUniswapV2Router02(router).swapExactTokensForETHSupportingFeeOnTransferTokens(
                    amountIn,
                    1,
                    path,
                    address(this),
                    deadline
                );
            } else {
                IUniswapV2Router02(router).swapExactTokensForTokensSupportingFeeOnTransferTokens(
                    amountIn,
                    1,
                    path,
                    address(this),
                    deadline
                );
            }
        }

        uint amountAfter = tokenOut == address(weth9) ? address(this).balance : IERC20(tokenOut).balanceOf(address(this));
        require(amountAfter > amountBefore, "swap failed");

        return amountAfter - amountBefore;
    }

