    function getPairDetails(DexPlatform platform, address pair) internal view returns (address, address, uint24) {
        if (platform == DexPlatform.UniV3_Main) {
            IUniswapV3Pool poolV3 = IUniswapV3Pool(pair);
            return (poolV3.token0(), poolV3.token1(), poolV3.fee());
        } else {
            IUniswapV2Pair pairV2 = IUniswapV2Pair(pair);
            return (pairV2.token0(), pairV2.token1(), 0);
        }
    }

    function getAmountOut(DexPlatform platform, address pair, address tokenIn, uint amountIn) internal returns(address tokenOut, uint amountOut) {
        if (platform == DexPlatform.UniV3_Main) {
            IUniswapV3Pool poolV3 = IUniswapV3Pool(pair);
            tokenOut = tokenIn == poolV3.token0() ? poolV3.token1() : poolV3.token0();
            try getQuoter(platform).quoteExactInputSingle(tokenIn, tokenOut, poolV3.fee(), amountIn, 0) returns (uint _amountOut) {
                amountOut = _amountOut;
            } catch (bytes memory) {
                amountOut = 0;
            }
        } else {
            IUniswapV2Pair pairV2 = IUniswapV2Pair(pair);
            tokenOut = tokenIn == pairV2.token0() ? pairV2.token1() : pairV2.token0();

            address[] memory path = new address[](2);
            path[0] = tokenIn;
            path[1] = tokenOut;
            amountOut = IUniswapV2Router02(getRouter(platform)).getAmountsOut(amountIn, path)[1];
        }
    }

    function getRouter(DexPlatform platform) internal pure returns (address router) {
        if (platform == DexPlatform.UniV3_Main) {
            router = 0xE592427A0AEce92De3Edee1F18E0157C05861564;
        } else if (platform == DexPlatform.UniV2_Main) {
            router = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
        } else if (platform == DexPlatform.SushiV2_Main) {
            router = 0xd9e1cE17f2641f24aE83637ab66a2cca9C378B9F;
        } else if (platform == DexPlatform.PancakeV1_Bsc) {
            router = 0x05fF2B0DB69458A0750badebc4f9e13aDd608C7F;
        } else if (platform == DexPlatform.PancakeV2_Bsc) {
            router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        } else {
            revert("invalid platform");
        }
    }

    function getQuoter(DexPlatform platform) internal pure returns (IQuoter quoter) {
        if (platform == DexPlatform.UniV3_Main) {
            quoter = IQuoter(0xb27308f9F90D607463bb33eA1BeBb41C27CE5AB6);
        } else {
            revert("invalid paltform");
        }
    }

    function transferToken(address to, address token, uint256 amount) internal {
        bool isWeth = token == address(weth9);

        if (amount == 0) {
            if (isWeth) {
                amount = address(this).balance;
            } else {
                amount = IERC20(token).balanceOf(address(this));
            }
        } else {
            if (isWeth) {
                require(amount <= address(this).balance, "bad ether balance");
            } else {
                require(amount <= IERC20(token).balanceOf(address(this)), "bad token balance");
            }
        }

        require(amount > 0, "bad transfer amount");

        if (isWeth) {
            weth9.deposit{value: amount}();
        } 

        IERC20(token).transfer(to, amount);
    }

    function toAddress(bytes memory _bytes, uint256 _start) internal pure returns (address) {
        address tempAddress;
        assembly {
            tempAddress := div(mload(add(add(_bytes, 0x20), _start)), 0x1000000000000000000000000)
        }
        return tempAddress;
    }

    function toUint8(bytes memory _bytes, uint256 _start) internal pure returns (uint8) {
        uint8 tempUint;
        assembly {
            tempUint := mload(add(add(_bytes, 0x1), _start))
        }
        return tempUint;
    }

    function toDexPairs(bytes memory data) internal pure returns (DexPair[] memory pairs) {
        uint count = data.length / 21;
        pairs = new DexPair[](count);
        for (uint i=0; i<count; i++) {
            pairs[i].pair = toAddress(data, i * 21);
            pairs[i].platform = DexPlatform(toUint8(data, i * 21 + 20));
        }
    }
