// File: contracts/seigniorageReserve.sol

pragma solidity >=0.5.15;
pragma experimental ABIEncoderV2;





contract SeigniorageReserve is Ownable {
    using SafeMath for uint256;

    address public gov;
    address public pendingGov;

    event NewPendingGov(address oldPendingGov, address newPendingGov);
    event NewGov(address oldGov, address newGov);
    event TreasuryIncreased(uint256 reservesAdded, uint256 cashSold, uint256 cashFromReserves, uint256 cashToReserves);
    event ExecuteTransaction(bytes32 indexed txHash, address indexed target, uint value, string signature,  bytes data, uint eta);
    event LogWhitelistReserve(address asset, bool status);

    mapping (address => bool) public whitelistReserve;

    modifier onlyGov() {
        require(msg.sender == gov, 'unauthorized');
        _;
    }

    function initialize(
        address owner_,
        address timelock_,
        address USDxContract_
    )
        public
        initializer
    {
        Ownable.initialize(owner_);
        whitelistReserve[USDxContract_] = true;

        gov = timelock_;
    }

    /** @notice sets the pendingGov
     * @param pendingGov_ The address of the gov contract to use for authentication.
     */
    function _setPendingGov(address pendingGov_)
        external
        onlyGov
    {
        address oldPendingGov = pendingGov;
        pendingGov = pendingGov_;
        emit NewPendingGov(oldPendingGov, pendingGov_);
    }

    function _setWhitelistReserve(address asset, bool status)
        external 
        onlyGov
    {
        whitelistReserve[asset] = status;
        emit LogWhitelistReserve(asset, status);
    }

    /**
     * @notice lets msg.sender accept governance
     */
    function _acceptGov()
        external
    {
        require(msg.sender == pendingGov, "!pending");
        address oldGov = gov;
        gov = pendingGov;
        pendingGov = address(0);
        emit NewGov(oldGov, gov);
    }

    /// @notice Moves all tokens to a new reserve contract
    function migrateReserves(
        address newReserve,
        address[] memory tokens
    )
        public
        onlyGov
    {
        for (uint256 i = 0; i < tokens.length; i++) {
            IERC20 token = IERC20(tokens[i]);
            uint256 bal = token.balanceOf(address(this));
            SafeERC20.safeTransfer(token, newReserve, bal);
        }
    }

    function sendToken(address token, uint256 amount, address destination) public onlyGov {
        require(whitelistReserve[token], 'SeigniorageReserve::asset must be whitelisted first');
        SafeERC20.safeTransfer(IERC20(token), destination, amount);
    }

    function executeTransaction(address target, uint value, string memory signature, bytes memory data, uint eta) public payable onlyGov returns (bytes memory) {
        bytes32 txHash = keccak256(abi.encode(target, value, signature, data, eta));
        bytes memory callData;

        if (bytes(signature).length == 0) {
            callData = data;
        } else {
            callData = abi.encodePacked(bytes4(keccak256(bytes(signature))), data);
        }

        // solium-disable-next-line security/no-call-value
        (bool success, bytes memory returnData) = target.call.value(value)(callData);
        require(success, "SeigniorageReserve::executeTransaction: Transaction execution reverted.");

        emit ExecuteTransaction(txHash, target, value, signature, data, eta);

        return returnData;
    }
}
