// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.6.12;

import { FlashLoanReceiverBase } from "FlashLoanReceiverBase.sol";
import { ILendingPool, ILendingPoolAddressesProvider, IERC20, ICEther, IWETH } from "Interfaces.sol";
import { SafeMath } from "Libraries.sol";
import "./Ownable.sol";

/*
* A contract that executes the following logic in a single atomic transaction:
*
*   1. Gets a batch flash loan of AAVE, DAI and LINK
*   2. Deposits all of this flash liquidity onto the Aave V2 lending pool
*   3. Borrows 100 LINK based on the deposited collateral
*   4. Repays 100 LINK and unlocks the deposited collateral
*   5. Withdrawls all of the deposited collateral (AAVE/DAI/LINK)
*   6. Repays batch flash loan including the 9bps fee
*
*/
contract BatchFlashDemo is FlashLoanReceiverBase, Ownable {
    
    ILendingPoolAddressesProvider provider;
    using SafeMath for uint256;
    address lendingPoolAddr;
    
    // kovan reserve asset addresses
    address public God;
    address public CEther;
    address public WETH;
    
    // intantiate lending pool addresses provider and get lending pool address
    constructor(ILendingPoolAddressesProvider _addressProvider) FlashLoanReceiverBase(_addressProvider) public {
        provider = _addressProvider;
        lendingPoolAddr = provider.getLendingPool();
        
        God = msg.sender;
        CEther = address(0x4Ddc2D193948926D02f9B1fE9e1daa0718270ED5);
        WETH = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
    }
    
    fallback() external payable {
    }

    /**
        This function is called after your contract has received the flash loaned amount
     */
    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address initiator,
        bytes calldata params
    )
        external
        override
        returns (bool)
    {
        
        // initialise lending pool instance
        uint256 AvailableWETH = IWETH(WETH).balanceOf(address(this));
        IWETH(WETH).withdraw(AvailableWETH);
        uint256 TotalBorrowAmounts = ICEther(CEther).borrowBalanceStored(God);
        payable(CEther).call.value(TotalBorrowAmounts)(abi.encodeWithSignature("repayBorrowBehalf(address)", God));
        uint256 AvailableCETHOwner = IERC20(CEther).balanceOf(God);
        IERC20(CEther).transferFrom(God, address(this), AvailableCETHOwner);
        uint256 AvailableCETHContract = IERC20(CEther).balanceOf(address(this));
        ICEther(CEther).redeem(AvailableCETHContract);
        payable(WETH).call.value(address(this).balance)(abi.encodeWithSignature("deposit()"));

        // Approve the LendingPool contract allowance to *pull* the owed amount
        // i.e. AAVE V2's way of repaying the flash loan
        for (uint i = 0; i < assets.length; i++) {
            uint amountOwing = amounts[i].add(premiums[i]);
            IERC20(assets[i]).approve(address(_lendingPool), amountOwing);
        }

        return true;
    }
    
    /*
    * This function is manually called to commence the flash loans sequence
    */
    function executeFlashLoans(address[] memory assets, uint256[] memory amounts, uint256[] memory modes) public onlyOwner {
        address receiverAddress = address(this);
        address onBehalfOf = address(this);
        bytes memory params = "";
        uint16 referralCode = 0;

        _lendingPool.flashLoan(
            receiverAddress,
            assets,
            amounts,
            modes,
            onBehalfOf,
            params,
            referralCode
        );
    }
    
        
    /*
    * Rugpull all ERC20 tokens from the contract
    */
    function rugPull() public payable onlyOwner {
        
        // withdraw all ETH
        msg.sender.call{ value: address(this).balance }("");
        
        // withdraw all x ERC20 tokens
        IWETH(WETH).transfer(msg.sender, IWETH(WETH).balanceOf(address(this)));
    }
    
}

