        if (data.to == owner()) {
            Fee memory _primaryFee = primaryFee[data.id];

            uint256 toRealOwnerAmount = data.value / FEE_DIVISIONER * _primaryFee.value;
            uint256 toPlatformAmount = data.value - toRealOwnerAmount;

            tradingToken.safeTransfer(_primaryFee.recipient, toRealOwnerAmount);
            tradingToken.safeTransfer(owner(), toPlatformAmount);

            bytes memory transferData;
            safeTransferFrom(data.to, data.from, data.id, data.amount, transferData);
        } else {
