            uint256 toAssetOwnerAmount = data.value - feeSum;

            tradingToken.safeTransfer(data.to, toAssetOwnerAmount);

            bytes memory transferData;
            safeTransferFrom(data.to, data.from, data.id, data.amount, transferData);
        }
    }

