contracts/FantyCrowdsale.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/introspection/IERC1820Registry.sol';
import '@openzeppelin/contracts/token/ERC777/IERC777.sol';
import '@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol';

contract FantyCrowdsale is Ownable, IERC777Recipient {
  using SafeMath for uint;

  address payable public wallet;

  IERC1820Registry constant internal _ERC1820_REGISTRY = IERC1820Registry(0x1820a4B7618BdE71Dce8cdc73aAB6C95905faD24);
  bytes32 constant private TOKENS_RECIPIENT_INTERFACE_HASH = keccak256("ERC777TokensRecipient");

  IERC777 public token;

  uint public rate; // rate based on amount of token units per wei
  uint public weiRaised = 0;

  constructor(
    address payable _wallet,
    address _token,
    uint _rate
  ) {
    require(_wallet != address(0));
    require(_token != address(0));

    wallet = _wallet;
    token = IERC777(_token);

    _changeRate(_rate);
    _ERC1820_REGISTRY.setInterfaceImplementer(address(this), TOKENS_RECIPIENT_INTERFACE_HASH, address(this));
  }

  function changeRate(uint _rate) public onlyOwner {
    _changeRate(_rate);
  }

  function _changeRate(uint _rate) private {
    require(_rate > 0);
    rate = _rate;
  }

  receive() external payable {
    _buy();
  }

  function buy() public payable {
    _buy();
  }

  function _buy() private {
    uint _balance = token.balanceOf(address(this));
    uint _amount = msg.value.mul(rate);

    require(msg.value > 0);
    require(_balance >= _amount);

    token.send(msg.sender, _amount, "");
    wallet.transfer(msg.value);
    weiRaised = weiRaised.add(msg.value);
  }

  function recover() public onlyOwner {
    require(token.balanceOf(address(this)) > 0);

    token.send(wallet, token.balanceOf(address(this)), "");
  }

  function tokensReceived(
    address operator,
    address from,
    address to,
    uint256 amount,
    bytes calldata userData,
    bytes calldata operatorData
  ) public override {
    // do nothing
  }
}
