// ----------------------------------------------------------------------------
// Owned contract
// ----------------------------------------------------------------------------
contract Owned {
    address payable public owner;

    event OwnershipTransferred(address indexed _from, address indexed _to);

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function transferOwnership(address payable _newOwner) public onlyOwner {
        owner = _newOwner;
        emit OwnershipTransferred(msg.sender, _newOwner);
    }
}

// ----------------------------------------------------------------------------
// ERC Token Standard #20 Interface
// ----------------------------------------------------------------------------
interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address tokenOwner) external view returns (uint256 balance);
    function allowance(address tokenOwner, address spender) external view returns (uint256 remaining);
    function transfer(address to, uint256 tokens) external returns (bool success);
    function approve(address spender, uint256 tokens) external returns (bool success);
    function transferFrom(address from, address to, uint256 tokens) external returns (bool success);

    event Transfer(address indexed from, address indexed to, uint256 tokens);
    event Approval(address indexed tokenOwner, address indexed spender, uint256 tokens);
}

contract TokenLocking is Owned{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    
    address tokenAddress;
    uint256 locked;
    uint256 claimed;
    uint256 lockedDate;
    uint256 lockingPeriod = 1460 days; // 4 years (years domination is deprecated, so used 'days')
    uint256 releasePeriod = 365 days;  // 1 year  (years domination is deprecated, so used 'days')
    uint256 releasePercentage = 25; //25% is released per year
    
    event CLAIMED(uint256 amount);
    event LOCKED(uint256 amount);
    
    constructor(address _tokenAddress) public{
        owner = msg.sender;
        tokenAddress = _tokenAddress;
    }
    
    function LOCK(uint256 amount) public onlyOwner{
        // check the balance of the contract address should be zero 
        require(IERC20(tokenAddress).balanceOf(address(this)) == 0, "already running locked instance");
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), amount);
        
        locked = amount;
        claimed = 0;
        lockedDate = block.timestamp;
        
        emit LOCKED(amount);
    }
    
    function CLAIM() public onlyOwner{
        uint256 totalTime = block.timestamp.sub(lockedDate); // this will give total time 
        totalTime = totalTime.div(releasePeriod); 
        uint256 totalPercentage = totalTime.mul(releasePercentage);
        uint256 totalTokensToClaim = onePercent(locked).mul(totalPercentage);
        totalTokensToClaim = totalTokensToClaim.sub(claimed);
        
        require(totalTokensToClaim > 0, "Nothing pending to claim");
        
        IERC20(tokenAddress).safeTransfer(msg.sender, totalTokensToClaim);
        
        claimed += totalTokensToClaim;
        
        emit CLAIMED(totalTokensToClaim);
    }
    
    function releaseTimeLeft() public view returns(uint256 _timeLeftInSecs) {
        uint256 totalTime = block.timestamp.sub(lockedDate); // this will give total time 
        totalTime = totalTime.div(releasePeriod);
        uint256 timeLeft = 0;
        
        if(totalTime <= 4)
            timeLeft = (lockedDate.add(releasePeriod.mul(totalTime.add(1)))).sub(block.timestamp);
        
        return timeLeft;
    }
    
    function onePercent(uint256 _tokens) internal pure returns (uint256){
        uint256 roundValue = _tokens.ceil(100);
        uint onePercentofTokens = roundValue.mul(100).div(100 * 10**uint(2));
        return onePercentofTokens;
    }
}
