pragma solidity 0.6.12;

library UniswapV2Library {
    using SafeMathUniswap for uint256;

    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB)
        internal
        pure
        returns (address token0, address token1)
    {
        require(tokenA != tokenB, "UniswapV2Library: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        require(token0 != address(0), "UniswapV2Library: ZERO_ADDRESS");
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(
        address factory,
        address tokenA,
        address tokenB
    ) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        pair = address(
            uint256(
                keccak256(
                    abi.encodePacked(
                        hex"ff",
                        factory,
                        keccak256(abi.encodePacked(token0, token1)),
                        hex"96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f" // init code hash
                    )
                )
            )
        );
    }

    // fetches and sorts the reserves for a pair
    function getReserves(
        address factory,
        address tokenA,
        address tokenB
    ) internal view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(
            IUniswapV2Factory(factory).getPair(tokenA, tokenB)
        )
            .getReserves();
        (reserveA, reserveB) = tokenA == token0
            ? (reserve0, reserve1)
            : (reserve1, reserve0);
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (uint256 amountB) {
        require(amountA > 0, "UniswapV2Library: INSUFFICIENT_AMOUNT");
        require(
            reserveA > 0 && reserveB > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        amountB = amountA.mul(reserveB) / reserveA;
    }

    // given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountOut) {
        require(amountIn > 0, "UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        uint256 amountInWithFee = amountIn.mul(997);
        uint256 numerator = amountInWithFee.mul(reserveOut);
        uint256 denominator = reserveIn.mul(1000).add(amountInWithFee);
        amountOut = numerator / denominator;
    }

    // given an output amount of an asset and pair reserves, returns a required input amount of the other asset
    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountIn) {
        require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        uint256 numerator = reserveIn.mul(amountOut).mul(1000);
        uint256 denominator = reserveOut.sub(amountOut).mul(997);
        amountIn = (numerator / denominator).add(1);
    }

    // performs chained getAmountOut calculations on any number of pairs
    function getAmountsOut(
        address factory,
        uint256 amountIn,
        address[] memory path
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;
        for (uint256 i; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i],
                path[i + 1]
            );
            amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
        }
    }

    // performs chained getAmountIn calculations on any number of pairs
    function getAmountsIn(
        address factory,
        uint256 amountOut,
        address[] memory path
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[amounts.length - 1] = amountOut;
        for (uint256 i = path.length - 1; i > 0; i--) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i - 1],
                path[i]
            );
            amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut);
        }
    }
}

// File: contracts/FarmerHelper.sol

pragma solidity 0.6.12;

interface IVault {
    function depositFor(
        address _depositFor,
        uint256 _pid,
        uint256 _amount,
        uint256 _lockTime
    ) external;
}

contract FarmHelper is OwnableUpgradeSafe {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    IVault public _tropVault;
    IWETH public _WETH;
    IUniswapV2Factory public _uniV2Factory;
    IUniswapV2Router02 public _uniV2Router;

    function initialize(address _vault) public initializer {
        OwnableUpgradeSafe.__Ownable_init();
        _uniV2Factory = IUniswapV2Factory(
            0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f
        );
        _uniV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );
        _WETH = IWETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
        _tropVault = IVault(_vault);
    }

    fallback() external payable {
        if (msg.sender != address(_WETH)) {
            revert();
        }
    }

    function buyTokenWithWeth(uint256 _wethAmount, address _token) internal {
        address pair = _uniV2Factory.getPair(address(_WETH), _token);
        buyTokenWithWethPair(_wethAmount, _token, IUniswapV2Pair(pair));
    }

    function buyTokenWithWethPair(
        uint256 _wethAmount,
        address _token,
        IUniswapV2Pair _pair
    ) internal {
        address[] memory path = new address[](2);
        path[0] = address(_WETH);
        path[1] = _token;
        _WETH.approve(address(_uniV2Router), _wethAmount);
        _uniV2Router.swapExactTokensForTokens(
            _wethAmount,
            0,
            path,
            address(this),
            block.timestamp + 100
        );
    }

    function addLiquidityByTokenForPool(
        uint256 pid,
        address token0,
        address token1,
        uint256 lockedTime,
        address payable to,
        bool autoStake
    ) public payable {
        uint256 buyAmount = msg.value.div(2);
        require(buyAmount > 0, "Insufficient ETH amount");
        _WETH.deposit{value: msg.value}();

        if (token0 == address(_WETH)) {
            buyTokenWithWeth(buyAmount, token1);
        } else if (token1 == address(_WETH)) {
            buyTokenWithWeth(buyAmount, token0);
        } else {
            buyTokenWithWeth(buyAmount, token0);
            buyTokenWithWeth(buyAmount, token1);
        }

        addLiquidity(token0, token1);

        IERC20 lp = IERC20(_uniV2Factory.getPair(token0, token1));
        uint256 balLP = lp.balanceOf(address(this));
        if (autoStake) {
            lp.safeApprove(address(_tropVault), balLP);
            _tropVault.depositFor(to, pid, balLP, lockedTime);
        } else {
            lp.safeTransfer(to, balLP);
        }

        IERC20(token0).transfer(to, IERC20(token0).balanceOf(address(this)));
        IERC20(token1).transfer(to, IERC20(token1).balanceOf(address(this)));
    }

    function addLiquidity(address token0, address token1) internal {
        uint256 token0Balance = IERC20(token0).balanceOf(address(this));

        uint256 token1Balance = IERC20(token1).balanceOf(address(this));

        IERC20(token0).safeApprove(address(_uniV2Router), token0Balance);
        IERC20(token1).safeApprove(address(_uniV2Router), token1Balance);

        _uniV2Router.addLiquidity(
            token0,
            token1,
            token0Balance,
            token1Balance,
            0,
            0,
            address(this),
            block.timestamp + 100
        );
    }
}
