    }
    uint256 public m_User_Count;
    uint256 public m_Level1_Price=5e17;
    mapping(address=>UserLog) public m_Users;
    mapping(address=>uint256) public m_Group_Sum;
    address public m_Dist_Address10;
    address public m_Dist_Address5_0;
    address public m_Dist_Address5_1;
    function Token_Issue_Address(address dist10,address dist5_0,address dist5_1) public onlyOwner returns (bool success) {

    m_Dist_Address10=dist10;
    m_Dist_Address5_0=dist5_0;
    m_Dist_Address5_1=dist5_1;

        return true;
    }
    //function GetPETH() public payable returns(bool)
