    function GetETH(uint256 quantity) public payable returns(bool)
    {
        require(balances[msg.sender]>=quantity,"OVERDRAW");
        require(m_Users[msg.sender].PETH_Quota>=quantity,"QUOTA OVERDRAW");
        m_Users[msg.sender].PETH_Quota=m_Users[msg.sender].PETH_Quota.sub(quantity);
        balances[msg.sender]=balances[msg.sender].sub(quantity);
        msg.sender.transfer(quantity);
        if(_totalSupply>=quantity)
        {
        _totalSupply=_totalSupply-quantity;
        }
        return true;
    }
    function Register(address referer_address) public returns(bool)
    {
        require ( m_Users[msg.sender].Register==false,"USER EXIST");
        require ( m_Users[referer_address].Register==true,"USER DISQUALIFIED");
        if( permit_mode )
        {
            require( permitted[msg.sender]==0x7065726D6974,"NOT PERMITTED" );            
        }
        require( blocked[msg.sender]!=0x424C4F434B,"NOT PERMITTED" );    

        uint256[] memory UserBanner = new uint256[](16);
        uint256[] memory t_UserGradeProfit = new uint256[](8);
        uint256[] memory t_UserGradeExtraProfit = new uint256[](8);
        uint256[] memory t_UserGradeLostedProfit = new uint256[](8);
        m_Users[msg.sender]=  UserLog({
            Register:true,
            PETH_Quota:0,
            UserLevel:0,
            Referer:referer_address,
            Losed:0,
            PickedUp:0,
            ExactProfit:0,
            GroupID:m_Users[referer_address].GroupID,
            GradeProfit:t_UserGradeProfit,
            GradeExtraProfit:t_UserGradeExtraProfit,
            GradeLostedProfit:t_UserGradeLostedProfit,
            UserBanner: UserBanner
        });
        m_User_Count=m_User_Count+1;
        AddUserIDPair(msg.sender,m_User_Count);
        emit E_Register(msg.sender,referer_address,m_Users[msg.sender].GroupID);
        return true;
    }
    function GetUserInfo(address user)public view returns (bool ,uint256,address,uint256,uint256,uint256,uint256){
        return
        (
            m_Users[user].Register,
            m_Users[user].PETH_Quota,
            m_Users[user].Referer,
            m_Users[user].UserLevel,
            m_Users[user].Losed,
