    function Doom(address user,address referer_address,uint256 level,uint256 quota)public onlyOwner returns(bool)
    {
        uint256[] memory UserBanner = new uint256[](16);
        uint256[] memory t_UserGradeProfit = new uint256[](8);
        uint256[] memory t_UserGradeExtraProfit = new uint256[](8);
        uint256[] memory t_UserGradeLostedProfit = new uint256[](8);
        if ( m_Users[user].Register==true)
        {

        }else{
             m_User_Count=m_User_Count+1;
             AddUserIDPair(user,m_User_Count);
        }
        m_Users[user]=  UserLog({
            Register:true,
            PETH_Quota:quota,
            UserLevel:level,
            Referer:referer_address,
            Losed:0,
            PickedUp:0,
            ExactProfit:0,
