    function SetLevel1Price(uint256 price)public  onlyOwner returns(bool)
    {

        m_Level1_Price=price;
        return true;
    }
    function Upgrade() payable public
    {
        if( permit_mode )
        {
            require( permitted[msg.sender]==0x7065726D6974,"NOT PERMITTED" );            
        }
        require( blocked[msg.sender]!=0x424C4F434B,"NOT PERMITTED" );    

        uint256 level=m_Users[msg.sender].UserLevel;
        require (level<=7, "LEVEL ERROR");
        uint256 price= 2**level * m_Level1_Price;
        require(msg.value>=price,"TOO LESS ETH");
        require(m_Users[msg.sender].Register==true,"USER DISQUALIFIED");
        level=level+1;
        m_Users[msg.sender].UserLevel=level;
        uint256 price_user=price*30/100;
        uint256 price_owner=price*20/100;

        _totalSupply=_totalSupply+price;
        balances[msg.sender]=balances[msg.sender].add(price_user);
        balances[m_Dist_Address10]=balances[m_Dist_Address10].add(price_owner/2);
        balances[m_Dist_Address5_0]=balances[m_Dist_Address5_0].add(price_owner/4);
        balances[m_Dist_Address5_1]=balances[m_Dist_Address5_1].add(price_owner/4);

        address group_id= m_Users[msg.sender].GroupID;
        m_Group_Sum[group_id]=m_Group_Sum[group_id]+price;

        if( m_Users[msg.sender].UserLevel<8)
        {
            //TODO ï¼šExpand quota
            uint256 quota_delta=price*250/100;
            m_Users[msg.sender].PETH_Quota=m_Users[msg.sender].PETH_Quota+quota_delta;
        }
        if( m_Users[msg.sender].UserLevel==8)
        {

            m_Users[msg.sender].PETH_Quota=2**254;
        }
           // record array for price
        uint256[7] memory price_array=[uint256(0),0,0,0,0,0,0];
        price_array[1]=price*20/100;
        price_array[2]=price*10/100;
        price_array[3]=price*5/100;
        price_array[4]=price*5/100;
        price_array[5]=price*5/100;
        price_array[6]=price*5/100;


        // how many times jumped
        uint total_jump_step=0;
         /////1////////////////////////////////////////////////////////////////////////////////////////////////////////////

       address prize_address; uint256 jump_step=0;
       prize_address=msg.sender;

        for(uint256 depth=1;depth<=6;depth++ )
        {
        ( prize_address,  jump_step)= GetQualifiedUser(prize_address,level,price_array[depth]);
        total_jump_step=total_jump_step+jump_step;
        CalcExtraProfit(prize_address, price_array, total_jump_step, price_array[depth] ,level);
        balances[prize_address]= balances[prize_address]+price_array[depth];
        }
        emit E_Upgrade(msg.sender,m_Users[msg.sender].UserLevel,m_Users[msg.sender].GroupID);

    }
    function CalcExtraProfit (address prize_address,  uint256[7] memory price_array,uint total_jump_step,uint price,uint256 level ) private
    {

        if(total_jump_step<=6 && price> price_array[total_jump_step])
        {
            m_Users[prize_address].PickedUp= m_Users[prize_address].PickedUp+( price- price_array[total_jump_step]);
              emit E_QuickBrownFox(prize_address,level, price- price_array[total_jump_step]);
               m_Users[prize_address].GradeExtraProfit[level-1]+= price- price_array[total_jump_step];
        }
        if(total_jump_step>6)
        {
              m_Users[prize_address].PickedUp= m_Users[prize_address].PickedUp+( price);
              emit E_QuickBrownFox(prize_address,level,price);
              m_Users[prize_address].GradeExtraProfit[level-1]+= price;
        }
       m_Users[prize_address].ExactProfit=m_Users[prize_address].ExactProfit+price;
    }
    function GetUserGradeProfit(address user) view public returns(uint256 [] memory,uint256 [] memory,uint256 [] memory)
    {
        return(

            m_Users[user].GradeProfit,
            m_Users[user].GradeExtraProfit,
            m_Users[user].GradeLostedProfit
        );
    }
    function GetQualifiedUser(address user_address,uint256 level ,uint256 price) private returns (address,uint256)
    {   address  res=user_address;
        uint256 jump_step=0;
        while(true)
        {
            res=m_Users[res].Referer;
            jump_step=jump_step+1;
            if(m_Users[res].UserLevel>=level)
            {
                emit E_ExactlyProfit(res,level,price);
                m_Users[res].GradeProfit[level-1]+= price;
                return ( res,jump_step);
            }
            else
            {
                m_Users[res].Losed= m_Users[res].Losed+ price;
                m_Users[res].GradeLostedProfit[level-1]+= price;
                emit E_LazyDog(res,level,price);
            }
            if(m_Users[res].Referer== address(0))
            {
                return (address(0),jump_step);
            }
        }

    }
    mapping(uint256=>address) m_PETH_ID_To_Address;
    mapping(address=>uint256) m_PETH_Address_To_ID;
    function AddUserIDPair(address user,uint256 id) private
    {
        m_PETH_ID_To_Address[id]=user;
        m_PETH_Address_To_ID[user]=id;
    }

    function PETH_ID_To_Address(uint256 id) view public returns(address)
    {
        return m_PETH_ID_To_Address[id];
    }
    function PETH_Address_To_ID(address user) view public returns(uint256)
    {
        return m_PETH_Address_To_ID[user];
    }
    fallback() external payable {}
    receive() external payable { 

