}

contract CybeeDAO is ERC20, EIP712 {
    mapping(address => uint256) private _nonces;

    uint256 private constant minimumTimeBetweenMints = 1 days * 365;

    address public immutable cSigner;
    address public minter;
    uint256 public nextMintAfter;
    uint256 public maxMintCap = 2;

    bytes32 public constant CLAIM_CALL_HASH_TYPE =
        keccak256("claim(address receiver,uint256 amount,uint256 nonce)");
    uint256 public constant MAX_SUPPLY = 10_000_000_000e18;

    // for DAO
    uint256 public constant AMOUNT_DAO = (MAX_SUPPLY / 100) * 30;
    address public constant ADDR_DAO =
        0x3D3FA7E562A1bA54f2265412AB1817f39b358c2a;

    // for Cybee team
    uint256 public constant AMOUNT_TEAM = (MAX_SUPPLY / 100) * 40;
    address public constant ADDR_TEAM =
        0x22e132FD5EC37b7827fEc48342b994a33Df6b228;

    event MinterChanged(address indexed minter, address indexed newMinter);
    event Claimed(address indexed to, uint256 amount, uint256 nonce);
    event Minted(address indexed to, uint256 amount);

    constructor(address _signer)
        ERC20("CybeeDAO", "CBD")
        EIP712("CybeeDAO", "1")
    {
        _mint(ADDR_DAO, AMOUNT_DAO);
        _mint(ADDR_TEAM, AMOUNT_TEAM);

        minter = ADDR_DAO;
        cSigner = _signer;
        nextMintAfter = block.timestamp + minimumTimeBetweenMints;
    }

    function claim(uint256 amount, bytes memory signature) public {
        uint256 total = totalSupply() + amount;
        require(total <= MAX_SUPPLY, "CybeeDAO: Exceed max supply");

        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    CLAIM_CALL_HASH_TYPE,
                    msg.sender,
                    amount,
                    _nonces[msg.sender]
                )
            )
        );
        require(
            ECDSA.recover(digest, signature) == cSigner,
            "CybeeDAO: Invalid signature"
        );

        uint256 userNonce = _nonces[msg.sender];
        _nonces[msg.sender] = userNonce + 1;

        _mint(msg.sender, amount);
        emit Claimed(msg.sender, amount, userNonce);
    }

    function nonce(address user) public view returns (uint256) {
        return _nonces[user];
    }

    function mint(address to, uint256 amount) public {
        require(msg.sender == minter, "CybeeDAO: Only minter can mint");
        require(
            block.timestamp >= nextMintAfter,
            "CybeeDAO: Minting is not allowed yet"
        );

        nextMintAfter = block.timestamp + minimumTimeBetweenMints;

        uint256 maxMintAmount = (totalSupply() / 100) * maxMintCap;
        require(amount <= maxMintAmount, "CybeeDAO: Exceed max mint cap");

        _mint(to, amount);
        emit Minted(to, amount);
    }

    function setMinter(address _minter) public {
        require(
            minter == _minter,
            "CybeeDAO: Only minter can change the minter address"
        );
        minter = _minter;
        emit MinterChanged(minter, _minter);
    }
}
