    /** ADMIN */

    function setKongIsland(address _KongIsland) external onlyOwner {
        kongIsland = IKongIsland(_KongIsland);
        ecosystem[_KongIsland] = true;
    }

    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function setPhases(uint256 _paidTokens, uint256 _LATE_PHASE) external onlyOwner {
        PAID_TOKENS = _paidTokens;
        LATE_PHASE = _LATE_PHASE;
    }

    function setPaused(bool _paused) external onlyOwner {
        if (_paused) _pause();
        else _unpause();
    }

  function setMintPrice(uint256 mintprice) external onlyOwner{
        MINT_PRICE = mintprice;
    }

  function updateStartTime(uint256 _startTime) external onlyOwner{
        startTime = _startTime;
    }

  function updatecontracts(address _food,address _itraits) external onlyOwner {
        food = IFOOD(_food);
        traits = ITraits(_itraits);
        ecosystem[_itraits] = true;
    }

    function addToReservedMinters(address _reserved, uint256 _amount) external onlyOwner {
        reservedMinters[_reserved].amount = _amount;
    }

    function bulkAddReserved(address[] calldata _reserved,uint256[] calldata _amount) external onlyOwner {
        for (uint256 i = 0; i < _reserved.length; i++) {
        reservedMinters[_reserved[i]].amount = _amount[i];
        }
    }
    /** RENDER */

  function tokenURI(uint256 tokenId) public view override returns (string memory) {
    require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
    require(ecosystem[msg.sender] || mintBlock[tokenId] + 1 < block.number);
    return traits.tokenURI(tokenId);
  }

}
