    uint256 constant stakingAmount = 100000000;
    uint256 constant numPhases = 10;
    
    uint256 [numPhases] phases = [1595203200, 1603152000, 1605830400, 1608422400, 1611100800, 1613779200, 1616198400, 1618876800, 1621468800, 1624147200];
    uint256 lastRewardStamp;
    
    /*
    phase0 : 1595203200 20.07.2020 00:00 UTC 30%
    phase1 : 1603152000 20.10.2020 00:00 UTC 27%
    phase2 : 1605830400 20.11.2020 00:00 UTC 24%
    phase3 : 1608422400 20.12.2020 00:00 UTC 21%
    phase4 : 1611100800 20.01.2021 00:00 UTC 18%
    phase5 : 1613779200 20.02.2021 00:00 UTC 15%
    phase6 : 1616198400 20.03.2021 00:00 UTC 12%
    phase7 : 1618876800 20.04.2021 00:00 UTC 9%
    phase8 : 1621468800 20.05.2021 00:00 UTC 6%
    phase9 : 1624147200 20.06.2021 00:00 UTC 3%
    */
    
    address[] internal stakeholders;
    mapping(address => uint256) internal rewards;
    mapping(address => uint256) internal stakes;
  
    constructor() Ownable() ERC20('MaxidaxV1.3.1','MAXI') public
    {
        _setupDecimals(numDecimals);
        _mint(owner(), totalFreeSupply);
        lastRewardStamp = block.timestamp;
    }
    
    function addStakeholder(address _stakeholder) private
    {
       (bool _isStakeholder, ) = isStakeholder(_stakeholder);
       if(!_isStakeholder) stakeholders.push(_stakeholder);
    }
    
    function isStakeholder(address _address) public view returns(bool, uint256)
    {
        for (uint256 s = 0; s < stakeholders.length; s++){
           if (_address == stakeholders[s]) return (true, s);
        }
        return (false, 0);
    }
    
    function removeStakeholder(address _stakeholder) private
    {
       (bool _isStakeholder, uint256 s) = isStakeholder(_stakeholder);
       if(_isStakeholder){
           stakeholders[s] = stakeholders[stakeholders.length - 1];
           stakeholders.pop();
       }
    }

    function stakeOf(address _stakeholder) public view returns(uint256)
    {
       return stakes[_stakeholder];
    }
    
    function getLastRewardPeriod() public view returns(uint256)
    {
       return lastRewardStamp;
    }
    
    function getNumberOfStakeholders() public view returns(uint256)
    {
       return stakeholders.length;
    }

    function totalStakes() public view returns(uint256)
    {
            uint256 _totalStakes = 0;
            for (uint256 s = 0; s < stakeholders.length; s++){
                _totalStakes = _totalStakes.add(stakes[stakeholders[s]]);
            }
            return _totalStakes;
    }
    


    function createSingleStake(uint256 _stake) public
    {
        if (_stake == stakingAmount)
        {
            _burn(msg.sender, _stake);
            if(stakes[msg.sender] == 0) 
                addStakeholder(msg.sender);
            stakes[msg.sender] = stakes[msg.sender].add(_stake);
        }
    }
   
		function removeStake(address _stakeholder) public onlyOwner
		{
			 _mint(_stakeholder, stakes[_stakeholder]);
			 stakes[_stakeholder] = 0;
			 removeStakeholder(_stakeholder);	 
		}
   	
		function rewardOf(address _stakeholder) public view returns(uint256)
		{
			 return rewards[_stakeholder];
		}

		function totalRewards() public view returns(uint256)
		{
			 uint256 _totalRewards = 0;
			 for (uint256 s = 0; s < stakeholders.length; s++){
				   _totalRewards = _totalRewards.add(rewards[stakeholders[s]]);
			 }
			 return _totalRewards;
		}

		function getCurrentRate() public view returns(uint256)
		{
			 assert(block.timestamp > phases[0]);
			 
			 uint256 phase = 1;
			 while ((phases[phase] < block.timestamp) && (phase < numPhases)) 
				phase.add(1);
				
			 return numPhases.sub(phase.sub(1)); 
		}

		function distributeDailyRewards() public
		{
			require(block.timestamp > (lastRewardStamp + 3600*24), "Can be called only once per day");
			uint256 currentRate = getCurrentRate();
			
			for (uint256 s = 0; s < stakeholders.length; s++)
				 rewards[stakeholders[s]] = rewards[stakeholders[s]].add(SafeMath.mul(SafeMath.div(stakes[stakeholders[s]],1000),currentRate));
				 
			lastRewardStamp = block.timestamp;
		}

		function withdrawReward() public
		{
			uint256 reward = rewards[msg.sender];
			rewards[msg.sender] = 0;
			_mint(msg.sender, reward);
		}
		
		function pauseContract() onlyOwner public {
				_pause();
		}

		function unpauseContract() onlyOwner public {
				_unpause();
		}

		function transfer(address _to, uint256 _value) public isNotBlacklisted override returns (bool) {
				return super.transfer(_to, _value);
		}

		function approve(address _spender, uint256 _value) public isNotBlacklisted override returns (bool) {
				return super.approve(_spender, _value);
		}

		function transferFrom(address _from, address _to, uint256 _value) public isNotBlacklisted override returns (bool) {
				return super.transferFrom(_from, _to, _value);
		}        
}
