pragma solidity ^0.6.6;


contract Proxiable {
    // Code position in storage is keccak256("PROXIABLE") = "0xc5f16f0fcc639fa48a6947836d9850f504798523bf8c9a3a87d5876cf622bcf7"

    function updateCodeAddress(address newAddress) internal {
        require(
            bytes32(0xc5f16f0fcc639fa48a6947836d9850f504798523bf8c9a3a87d5876cf622bcf7) == Proxiable(newAddress).proxiableUUID(),
            "Not compatible"
        );
        assembly { // solium-disable-line
            sstore(0xc5f16f0fcc639fa48a6947836d9850f504798523bf8c9a3a87d5876cf622bcf7, newAddress)
        }
    }
    function proxiableUUID() public pure returns (bytes32) {
        return 0xc5f16f0fcc639fa48a6947836d9850f504798523bf8c9a3a87d5876cf622bcf7;
    }
}

contract LibraryLockDataLayout {
  bool public initialized = false;
}

contract LibraryLock is LibraryLockDataLayout {
    // Ensures no one can manipulate the Logic Contract once it is deployed.
    // PARITY WALLET HACK PREVENTION

    modifier delegatedOnly() {
        require(initialized == true, "The library is locked. No direct 'call' is allowed");
        _;
    }
    function initialize() internal {
        initialized = true;
    }
}

contract DevFundDataLayout is LibraryLock {
    address owner;
    
    struct claimLock{
        uint256 nextClaimBlock;
        uint32 devPercentage;
    }
    mapping(address => claimLock) public devClaimMap;
    
    using SafeMath for uint32;
    using SafeMath for uint256;
}

contract NyanDevFund is Proxiable, DevFundDataLayout {
    
    constructor() public {
        
    }
    
    modifier _onlyOwner() {
        require(msg.sender == owner);
        _;
    }
    
    function devConstructor(address _owner) public {
        require(!initialized);
        owner = _owner;
        devClaimMap[owner].devPercentage = 50;
        initialize();
    }
    
    function updateCode(address newCode) public _onlyOwner delegatedOnly  {
        updateCodeAddress(newCode);
        
        // emit logicContractUpdated(newCode);
    }
    
    function assignDev(address dev, uint32 perc) public _onlyOwner {
        //assign the dev with a %
        devClaimMap[dev].devPercentage = perc;
    }
    
    function devClaim() public {
        //require that dev claim block is less than current block number
        require(devClaimMap[msg.sender].nextClaimBlock > block.number);
        //make sure claimant is dev and send them ETH
        require(devClaimMap[msg.sender].devPercentage > 0);
        uint256 ETHToClaim = devClaimMap[msg.sender].devPercentage.mul(address(this).balance).div(100);
        msg.sender.call{value: ETHToClaim}("");
        devClaimMap[msg.sender].nextClaimBlock = devClaimMap[msg.sender].nextClaimBlock.add(45500);
    }
    
    receive() external payable {
        
    }
}
