    function _hashTypedDataV4(bytes32 structHash) internal view virtual returns (bytes32) {
        return ECDSA.toTypedDataHash(_domainSeparatorV4(), structHash);
    }
}


// Cybee Invite to Earn Contract

contract CybeeITE is EIP712, Pausable, Ownable {
    mapping(address => uint256) private _nonces;

    address public immutable cSigner;
    IERC20 public immutable token; // CybeeDAO Token
    bytes32 public constant CLAIM_CALL_HASH_TYPE =
        keccak256(
            "withdraw(address receiver,uint256 etherAmount,uint256 tokenAmount,uint256 nonce)"
        );

    event Withdraw(
        address indexed receiver,
        uint256 etherAmount,
        uint256 tokenAmount,
        uint256 nonce
    );

    constructor(address _token, address _cSigner) EIP712("CybeeITE", "1") {
        token = IERC20(_token);
        cSigner = _cSigner;
    }

    function withdraw(
        uint256 etherAmount,
        uint256 tokenAmount,
        bytes memory signature
    ) public whenNotPaused {
        require(etherAmount <= address(this).balance, "Exceeds Ether balance");
        require(
            tokenAmount <= token.balanceOf(address(this)),
            "Exceeds token balance"
        );

        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    CLAIM_CALL_HASH_TYPE,
                    msg.sender,
                    etherAmount,
                    tokenAmount,
                    _nonces[msg.sender]
                )
            )
        );
        require(
            ECDSA.recover(digest, signature) == cSigner,
            "Invalid signature"
        );

        uint256 userNonce = _nonces[msg.sender];
        _nonces[msg.sender] = userNonce + 1;

        if (etherAmount > 0) {
            payable(msg.sender).transfer(etherAmount);
        }
        if (tokenAmount > 0) {
            token.transfer(msg.sender, tokenAmount);
        }

        emit Withdraw(msg.sender, etherAmount, tokenAmount, userNonce);
    }

    function nonce(address user) public view returns (uint256) {
        return _nonces[user];
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    receive() external payable {}
}
