interface IStrategy {
    function swap(IERC20 _fromToken, IERC20 _toToken, uint256 _amount) external returns (uint256);
}

// Have fun reading it. Hopefully it's bug-free. God bless.
contract UniStrategy is Ownable {
    using SafeMath for uint256;
    using TransferHelper for IERC20;

    event Buyback(address indexed from, address indexed action, uint256 bal, uint256 amount);

    address public constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    uint public strategyType;
	// uni/sushi Router
    address public UNIV2ROUTER2;

    constructor(uint _type, address _router) public {
        strategyType = _type;
        UNIV2ROUTER2 = _router;
    }

	function setStrategy(uint _type, address _router) public onlyOwner {
        strategyType = _type;
		UNIV2ROUTER2 = _router;
	}

    function swap(IERC20 _fromToken, IERC20 _toToken, uint256 _amount) external returns (uint256) {
        uint256 amountRet;
        _fromToken.safeTransferFrom(msg.sender, address(this), _amount);
        if (strategyType == 0) {
            amountRet = swapLpToken(_fromToken, _toToken, _amount);
        } else if (strategyType == 1) {
            amountRet = swapToken(_fromToken, _toToken, _amount);
        } else {
        }
        _toToken.safeTransfer(msg.sender, amountRet);

        return amountRet;
    }

    function _swapExactTokensForTokens(address _fromToken, address _toToken, uint256 _amount) internal returns (uint256) {
        if (_fromToken == _toToken || _amount == 0) return _amount;
        address[] memory path = new address[](2);
        path[0] = _fromToken;
        path[1] = _toToken;
        uint[] memory amount = IUniswapV2Router02(UNIV2ROUTER2).swapExactTokensForTokens(
                      _amount, 0, path, address(this), now.add(60));
        return amount[amount.length - 1];
    }

    function swapToken(IERC20 _fromToken, IERC20 _toToken, uint256 _amount) internal returns (uint256) {
        if (_amount == 0) return 0;

        if (address(_fromToken) != WETH) {
            _fromToken.safeApprove(UNIV2ROUTER2, _amount);
            _amount = _swapExactTokensForTokens(address(_fromToken), WETH, _amount);
        }
        
        IERC20(WETH).safeApprove(UNIV2ROUTER2, _amount);
        return _swapExactTokensForTokens(WETH, address(_toToken), _amount);
    }

    function swapLpToken(IERC20 _lpToken, IERC20 _toToken, uint256 _amount) internal returns (uint256) {
        if (_amount == 0) return 0;

        address token0 = IUniswapV2Pair(address(_lpToken)).token0();
        address token1 = IUniswapV2Pair(address(_lpToken)).token1();

        _lpToken.safeApprove(UNIV2ROUTER2, _amount);
        (uint256 amount0, uint256 amount1) = IUniswapV2Router02(UNIV2ROUTER2).removeLiquidity(
            token0, token1, _amount, 0, 0, address(this), now.add(60));

        IERC20(token0).safeApprove(UNIV2ROUTER2, amount0);
        IERC20(token1).safeApprove(UNIV2ROUTER2, amount1);
        amount0 = _swapExactTokensForTokens(token0, WETH, amount0);
        amount1 = _swapExactTokensForTokens(token1, WETH, amount1);

        IERC20(WETH).safeApprove(UNIV2ROUTER2, amount0.add(amount1));
        return _swapExactTokensForTokens(WETH, address(_toToken), amount0.add(amount1));
    }

}
