pragma solidity ^0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
// import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
// import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
// import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

// interface INFT {
//     function mintFrog (uint numberOfFrogs) external payable;
//     function purchase(uint256 numberOfTokens) external payable;
// }

contract NftFb is Ownable {
    constructor() {}

    address public tokenContract = address(0);
    bool public isActivated = false;
    uint256 public nftThreshold = 0;

    function addBribe() public payable {
    }

    function removeBribe(uint256 amount) public onlyOwner {
        require(amount <= address(this).balance, "amount must be <= than what the contract hodls");
        payable(msg.sender).transfer(amount);
    }

    function getPayed() public onlyOwner {
        require(isActivated, "not activated");

        // check for NFTs
        require(IERC721(tokenContract).balanceOf(owner()) >= nftThreshold, "NOT ENOUGH NFTS!");

        block.coinbase.transfer(address(this).balance);
        isActivated = false;
    }

    function activate(address contractAddress, uint256 threshold) public onlyOwner {
        tokenContract = contractAddress;
        nftThreshold = threshold;
        isActivated = true;
    }

    function deactivate() public onlyOwner {
        isActivated = false;
    }
}
