AlphaSorbetVault.sol.content:
// SPDX-License-Identifier: Unlicense

pragma solidity 0.7.6;
pragma abicoder v2;

import "./token/IERC20.sol";
import "./interfaces/IUniswapV3Pool.sol";
import "./interfaces/external/IWETH9.sol";
import "./utils/ReentrancyGuard.sol";
import './libraries/LiquidityAmounts.sol';
import './libraries/TickMath.sol';
import './libraries/TransferHelper.sol';
import "./libraries/PositionKey.sol";
import "./libraries/LowGasSafeMath.sol";
import "./libraries/LowGasSafeMath128.sol";
import "./libraries/Position.sol";
import './libraries/SqrtPriceMath.sol';

contract SorbetVault is ReentrancyGuard {
    using LowGasSafeMath for uint256;
    using LowGasSafeMath128 for uint128;
    using Position for Position.Info;
    
    struct MintCallbackData {
        address payer;
    }
    
    struct UserInfo {
        uint128 liquidity; 
        uint256 token0Rewards;
        uint256 token1Rewards;
        uint256 token0PerLiquidityPaid;
        uint256 token1PerLiquidityPaid;
    }
    
    event Deposit(
        address indexed sender,
        uint256 liquidity,
        uint256 amount0,
        uint256 amount1
    );
    
    event Withdraw(
        address indexed sender,
        uint256 liquidity,
        uint256 amount0,
        uint256 amount1
    );
    
    event CollectFees(
        uint256 feesFromPool0,
        uint256 feesFromPool1,
        uint256 usersFees0,
        uint256 usersFees1
    );
    
    event Rerange(
        int24 tickLower,
        int24 tickUpper,
        uint256 amount0,
        uint256 amount1
    );
    
    event RewardPaid(
        address indexed sender,
        uint256 fees0,
        uint256 fees1
    );
    
    event Snapshot(uint256 totalAmount0, uint256 totalAmount1);
    
    modifier onlyGovernance {
        require(msg.sender == governance, "NOT ALLOWED");
        _;
    }
    
    mapping(address => UserInfo) public userInfo; // Info of each user that stakes tokens.

    
    address public immutable token0;
    address public immutable token1;
    address public immutable weth;
    int24 public immutable tickSpacing;
    
    uint256 public protocolFee;
    uint256 public accruedProtocolFees0;
    uint256 public accruedProtocolFees1;
    uint256 public usersFees0;
    uint256 public usersFees1;
    uint256 public lastTimeUpdated;
    uint256 public token0PerLiquidityStored;
    uint256 public token1PerLiquidityStored;
    
    address public governance;
    IUniswapV3Pool public pool;
    
    uint128 public totalLiquidity;
    uint32 public twapDuration;
    int24 public tickLower;
    int24 public tickUpper;
    int24 public baseThreshold;
    int24 public maxTwapDeviation;
    
    bool public finalized;
    
    /**
     * @dev After deploying, strategy needs to be set via `setStrategy()`
     * @param _pool Underlying Uniswap V3 pool
     * @param _pool Underlying Uniswap V3 pool
     * @param _protocolFee Protocol fee expressed as multiple of 1e-4
     */
     constructor(
        address _pool,
        uint256 _protocolFee,
        uint32 _twapDuration,
        int24 _maxTwapDeviation
    ) {
        pool = IUniswapV3Pool(_pool);
        weth = pool.token0();
        token0 = pool.token0();
        token1 = pool.token1();
        tickSpacing = pool.tickSpacing();
        twapDuration = _twapDuration;
        maxTwapDeviation = _maxTwapDeviation;
        protocolFee = _protocolFee;

        governance = msg.sender;

        require(_protocolFee < 1e4, "protocolFee");
    }
    
    function init(address _strategy) external onlyGovernance {
        require(_strategy != address(0), "Strategy zero address");
        require(!finalized, "Finalized");
        finalized = true;
        baseThreshold = tickSpacing * 16;
        (, int24 currectTick, , , , , ) = pool.slot0();
        int24 tickFloor = _floor(currectTick);
        
        tickLower = tickFloor - baseThreshold;
        tickUpper = tickFloor + baseThreshold;
    }
    
    /**
     * @notice Deposits tokens in proportion to the vault's current holdings.
     * @dev These tokens sit in the vault and are not used for liquidity on
     * Uniswap until the next rebalance. Also note it's not necessary to check
     * if user manipulated price to deposit cheaper, as the value of range
     * orders can only by manipulated higher.
     * @param amount0Desired Max amount of token0 to deposit
     * @param amount1Desired Max amount of token1 to deposit
     * @param amount0Min Revert if resulting `amount0` is less than this
     * @param amount1Min Revert if resulting `amount1` is less than this
     * param to Recipient of shares
     * @return liquidity minted
     * @return amount0 Amount of token0 deposited
     * @return amount1 Amount of token1 deposited
     */
     function deposit(
        uint256 amount0Desired,
        uint256 amount1Desired,
        uint256 amount0Min,
        uint256 amount1Min
    )
        external
        payable
        nonReentrant
        updateVault(msg.sender)
        returns (
            uint128 liquidity,
            uint256 amount0,
            uint256 amount1
        )
    {
        require(amount0Desired > 0 || amount1Desired > 0, "amount0Desired or amount1Desired");
        UserInfo storage user = userInfo[msg.sender];
        // compute the liquidity amount
        {
            (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();
            uint160 sqrtRatioAX96 = TickMath.getSqrtRatioAtTick(tickLower);
            uint160 sqrtRatioBX96 = TickMath.getSqrtRatioAtTick(tickUpper);

            liquidity = LiquidityAmounts.getLiquidityForAmounts(
                sqrtPriceX96,
                sqrtRatioAX96,
                sqrtRatioBX96,
                amount0Desired,
                amount1Desired
            );
        }
        
        (amount0, amount1) = pool.mint(
            address(this),
            tickLower,
            tickUpper,
            liquidity,
            abi.encode(MintCallbackData({payer: msg.sender})));
        require(amount0 >= amount0Min && amount1 >= amount1Min, 'Price slippage check');
        user.liquidity = user.liquidity.add128(liquidity);
        totalLiquidity = totalLiquidity.add128(liquidity);
        emit Deposit(msg.sender, liquidity, amount0, amount1);
    }
    
    /**
     * @notice Withdraws tokens in proportion to the vault's holdings.
     * @dev Removes proportional amount of liquidity from Uniswap. Note it
     * doesn't collect share of fees since last rebalance to save gas.
     * @param liquidity burned by sender
     * @param amount0Min Revert if resulting `amount0` is smaller than this
     * @param amount1Min Revert if resulting `amount1` is smaller than this
     * @return amount0 Amount of token0 sent to recipient
     * @return amount1 Amount of token1 sent to recipient
     */
    function withdraw(
        uint128 liquidity,
        uint256 amount0Min,
        uint256 amount1Min
    ) external nonReentrant updateVault(msg.sender) returns (uint256 amount0, uint256 amount1) {
        require(liquidity > 0, "liquidity");
        UserInfo storage user = userInfo[msg.sender];
        require(user.liquidity >= liquidity, "Sorbetto: you cant eat that much popsicles");
        user.liquidity = user.liquidity.sub128(liquidity);
        totalLiquidity = totalLiquidity.sub128(liquidity);
        (amount0, amount1) = _burnLiquidityShare(liquidity, msg.sender);
        
        require(amount0 >= amount0Min, "amount0Min");
        require(amount1 >= amount1Min, "amount1Min");
        
        
        
        emit Withdraw(msg.sender, liquidity, amount0, amount1);
    }
    
    /**
     * @notice Updates vault's positions. Can only be called by the strategy.
     * @dev Two orders are placed - a base order and a limit order. The base
     * order is placed first with as much liquidity as possible. This order
     * should use up all of one token, leaving only the other one. This excess
     * amount is then placed as a single-sided bid or ask order.
     */
    function rerange() external nonReentrant updateVault(address(0)) {
        
        // Check price has not moved a lot recently. This mitigates price
        // manipulation during rebalance and also prevents placing orders
        // when it's too volatile.
        (, int24 tick, , , , , ) = pool.slot0();

        int24 twap = getTwap();
        int24 deviation = tick > twap ? tick - twap : twap - tick;
        require(deviation <= maxTwapDeviation, "maxTwapDeviation");
        
        _burnAllLiquidity();
        

        // Emit snapshot to record balances and supply
        uint256 balance0 = _balance0();
        uint256 balance1 = _balance1();
        emit Snapshot(balance0, balance1);

        (uint160 sqrtPriceX96, int24 currentTick, , , , , ) = pool.slot0();
        _setTicksForAmounts(sqrtPriceX96, currentTick);
        
        uint160 sqrtRatioAX96 = TickMath.getSqrtRatioAtTick(tickLower);
        uint160 sqrtRatioBX96 = TickMath.getSqrtRatioAtTick(tickUpper);

        uint128 liquidity = LiquidityAmounts.getLiquidityForAmounts(
            sqrtPriceX96,
            sqrtRatioAX96,
            sqrtRatioBX96,
            balance0,
            balance1
        );
        
        (uint256 amount0, uint256 amount1) = pool.mint(
            address(this),
            tickLower,
            tickUpper,
            liquidity,
            abi.encode(MintCallbackData({payer: address(this)})));
        
        emit Rerange(tickLower, tickUpper, amount0, amount1);
    }
    
    /// @dev Amount of token0 held as unused balance.
    function _balance0() internal view returns (uint256) {
        return IERC20(token0).balanceOf(address(this));
    }

    /// @dev Amount of token1 held as unused balance.
    function _balance1() internal view returns (uint256) {
        return IERC20(token1).balanceOf(address(this));
    }
    
    function _collectFees() internal returns (uint256 userCollect0, uint256 userCollect1) {
         // Do zero-burns to poke the Uniswap pools so earned fees are updated
        pool.burn(tickLower, tickUpper, 0);
        
        (uint256 collect0, uint256 collect1) =
            pool.collect(
                address(this),
                tickLower,
                tickUpper,
                type(uint128).max,
                type(uint128).max
            );
        uint256 earnedProtol0 = collect0.mul(protocolFee) / 1e4;
        uint256 earnedProtol1 = collect1.mul(protocolFee) / 1e4;
        accruedProtocolFees0 = accruedProtocolFees0.add(earnedProtol0);
        accruedProtocolFees1 = accruedProtocolFees1.add(earnedProtol1);
        userCollect0 = collect0.sub(earnedProtol0);
        userCollect1 = collect1.sub(earnedProtol1);
        usersFees0 = usersFees0.add(userCollect0);
        usersFees1 = usersFees1.add(userCollect1);
        emit CollectFees(collect0, collect1, usersFees0, usersFees1);
    }
    
    function _setTicksForAmounts(uint160 sqrtPriceX96, int24 currentTick) internal {
        
        int24 tickFloor = _floor(currentTick);
        
        int24 _tickLower = tickFloor - baseThreshold;
        int24 _tickUpper = tickFloor + baseThreshold;
        
        uint256 amount0Desired = _balance0();
        uint256 amount1Desired = _balance1();
        
        uint160 sqrtRatioAX96 = TickMath.getSqrtRatioAtTick(_tickLower);
        uint160 sqrtRatioBX96 = TickMath.getSqrtRatioAtTick(_tickUpper);
        
        uint128 liquidity = LiquidityAmounts.getLiquidityForAmounts(
            sqrtPriceX96,
            sqrtRatioAX96,
            sqrtRatioBX96,
            amount0Desired,
            amount1Desired);
                
        (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
            sqrtPriceX96,
            sqrtRatioAX96,
            sqrtRatioBX96,
            liquidity);
        bool upperDirection = _unusedAmountsDirection(amount0Desired, amount1Desired, amount0, amount1);
        
        if ( upperDirection) {
            uint160 nextSqrtPrice0 = SqrtPriceMath.getNextSqrtPriceFromAmount0RoundingUp(sqrtPriceX96, liquidity, amount0Desired, false);
            _tickUpper = _floor(TickMath.getTickAtSqrtRatio(nextSqrtPrice0));
        }
        else{
            uint160 nextSqrtPrice1 = SqrtPriceMath.getNextSqrtPriceFromAmount1RoundingDown(sqrtPriceX96, liquidity, amount1Desired, false);
            _tickLower = _floor(TickMath.getTickAtSqrtRatio(nextSqrtPrice1));
        }
        _checkRange(_tickLower, _tickUpper);
        tickUpper = _tickUpper;
        tickLower = _tickLower;
    }
    
    function _unusedAmountsDirection(uint256 amount0Desired, uint256 amount1Desired, uint256 amount0, uint256 amount1) internal pure returns (bool upperDirection) {
        uint256 unusedtoken0 = amount0Desired - amount0;
        uint256 unusedtoken1 = amount1Desired - amount1;
        
        uint256 p0;
        uint256 p1;
        if (amount0Desired == 0) p0 = 0;
        else p0 = unusedtoken0.mul(_token0DecimalPower()) / amount0Desired;
        if (amount1Desired == 0) p1 = 0;
        else p1 = unusedtoken1.mul(_token1DecimalPower()) / amount1Desired;
        if (p0 > p1) upperDirection = true;
    }
    
    function _token0DecimalPower() internal pure returns (uint256) {
        return 1e18; //WETH
    }
    function _token1DecimalPower() internal pure returns (uint256) {
        return 1e6; //USDT
    }
    
    function _burnLiquidityShare(
        uint128 liquidity,
        address to
    ) internal returns (uint256 amount0, uint256 amount1) {
        uint128 liquidityInPool = _positionLiquidity();
        require(liquidityInPool >= liquidity);
        (amount0, amount1) = pool.burn(tickLower, tickUpper, _toUint128(liquidity));

        if (amount0 > 0 || amount1 > 0) {
            // collect liquidity share including earned fees
            (amount0, amount0) = pool.collect(
                to,
                tickLower,
                tickUpper,
                _toUint128(amount0),
                _toUint128(amount1)
            );
        }
    }
    
    /// @dev Withdraws all liquidity in a range from Uniswap pool
    function _burnAllLiquidity() internal {
        
        // Burn all liquidity in this range
        uint256 amount0 = 0;
        uint256 amount1 = 0;
        uint128 liquidity = _positionLiquidity();
        if (liquidity > 0) {
            (amount0, amount1) = pool.burn(tickLower, tickUpper, liquidity);
        }
        
         // Collect all owed tokens
        pool.collect(
            address(this),
            tickLower,
            tickUpper,
            type(uint128).max,
            type(uint128).max
        );
    }
    
    function position() external view returns (uint128 liquidity, uint256 feeGrowthInside0LastX128, uint256 feeGrowthInside1LastX128, uint128 tokensOwed0, uint128 tokensOwed1) {
        bytes32 positionKey = PositionKey.compute(address(this), tickLower, tickUpper);
        (liquidity, feeGrowthInside0LastX128, feeGrowthInside1LastX128, tokensOwed0, tokensOwed1) = pool.positions(positionKey);
    }
    
    /// @dev Amount of liquidity in vault's position.
    function _positionLiquidity()
        internal
        view
        returns (uint128 liquidity)
    {
        bytes32 positionKey = PositionKey.compute(address(this), tickLower, tickUpper);
        (liquidity, , , , ) = pool.positions(positionKey);
    }
    
    /// @dev Casts uint256 to uint128 with overflow check.
    function _toUint128(uint256 x) internal pure returns (uint128) {
        assert(x <= type(uint128).max);
        return uint128(x);
    }
    
    /// @dev Rounds tick down towards negative infinity so that it's a multiple
    /// of `tickSpacing`.
    function _floor(int24 tick) internal view returns (int24) {
        int24 compressed = tick / tickSpacing;
        if (tick < 0 && tick % tickSpacing != 0) compressed--;
        return compressed * tickSpacing;
    }
    
    /// @dev Common checks for valid tick inputs.
    function _checkRange(int24 _tickLower, int24 _tickUpper) internal view {
        require(_tickLower < _tickUpper, "tickLower < tickUpper");
        require(_tickLower >= TickMath.MIN_TICK, "tickLower too low");
        require(_tickUpper <= TickMath.MAX_TICK, "tickUpper too high");
        require(_tickLower % tickSpacing == 0, "tickLower % tickSpacing");
        require(_tickUpper % tickSpacing == 0, "tickUpper % tickSpacing");
    }
    
    /// @dev Wrapper around `LiquidityAmounts.getLiquidityForAmounts()`.
    function _liquidityForAmounts(
        uint256 amount0,
        uint256 amount1
    ) internal view returns (uint128) {
        (uint160 sqrtRatioX96, , , , , , ) = pool.slot0();
        return
            LiquidityAmounts.getLiquidityForAmounts(
                sqrtRatioX96,
                TickMath.getSqrtRatioAtTick(tickLower),
                TickMath.getSqrtRatioAtTick(tickUpper),
                amount0,
                amount1
            );
    }
    
    // Pull in tokens from sender
    function uniswapV3MintCallback(
        uint256 amount0,
        uint256 amount1,
        bytes calldata data
    ) external {
        require(msg.sender == address(pool));
        MintCallbackData memory decoded = abi.decode(data, (MintCallbackData));
        if (amount0 > 0) pay(token0, decoded.payer, msg.sender, amount0);
        if (amount1 > 0) pay(token1, decoded.payer, msg.sender, amount1);
    }
    
    function pay(
        address token,
        address payer,
        address recipient,
        uint256 value
    ) internal {
        if (token == weth && address(this).balance >= value) {
            // pay with WETH9
            IWETH9(weth).deposit{value: value}(); // wrap only what is needed to pay
            IWETH9(weth).transfer(recipient, value);
        } else if (payer == address(this)) {
            // pay with tokens already in the contract (for the exact input multihop case)
            TransferHelper.safeTransfer(token, recipient, value);
        } else {
            // pull payment
            TransferHelper.safeTransferFrom(token, payer, recipient, value);
        }
    }
    
    /////dev
    
    /**
     * @notice Removes liquidity and transfer tokens to governance in case of
     * emergency. Cannot be called if already finalized.
     */
    function emergencyBurn(
        int24 _tickLower,
        int24 _tickUpper,
        uint128 liquidity
    ) external onlyGovernance {
        pool.burn(tickLower, tickUpper, liquidity);
        pool.collect(msg.sender, _tickLower, _tickUpper, type(uint128).max, type(uint128).max);
    }
    
    /**
     * @notice Transfers tokens to governance in case of emergency. Cannot be
     * called if already finalized.
     */
    function emergencyWithdraw(IERC20 token, uint256 amount) external onlyGovernance {
        IERC20(token).transfer(msg.sender, amount);
    }
    
    /**
     * @notice Used to collect accumulated protocol fees.
     */
    function collectProtocolFees(
        uint256 amount0,
        uint256 amount1
    ) external nonReentrant onlyGovernance {
        require(accruedProtocolFees0 >= amount0, "Amount0");
        require(accruedProtocolFees1 >= amount1, "Amount0");
        
        uint256 balance0 = _balance0();
        uint256 balance1 = _balance1();
        
        if (balance0 >= amount0 && balance1 >= amount1)
        {
            if (amount0 > 0) pay(token0, address(this), msg.sender, amount0);
            if (amount1 > 0) pay(token1, address(this), msg.sender, amount1);
        }
        else
        {
            uint128 liquidity = _liquidityForAmounts(amount0, amount1);
            (amount0, amount1) = _burnLiquidityShare(liquidity, msg.sender);
        
        }
        
        accruedProtocolFees0 = accruedProtocolFees0.sub(amount0);
        accruedProtocolFees1 = accruedProtocolFees1.sub(amount1);
        
    }
    
    function harvestFees() external updateVault(msg.sender) {
        UserInfo storage user = userInfo[msg.sender];
        uint256 balance0 = _balance0();
        uint256 balance1 = _balance1();
        uint256 reward0 = user.token0Rewards;
        uint256 reward1 = user.token1Rewards;
        if (balance0 >= reward0 && balance1 >= reward1) {
            user.token0Rewards = 0;
            user.token1Rewards = 0;
            if (reward0 > 0) pay(token0, address(this), msg.sender, reward0);
            if (reward1 > 0) pay(token1, address(this), msg.sender, reward1);
        }
        else {
            uint128 liquidity = _liquidityForAmounts(reward0, reward1);
            (reward0, reward1) = _burnLiquidityShare(liquidity, msg.sender);
        }
        emit RewardPaid(msg.sender, reward0, reward1);
    }
    
    // Function modifier that calls update reward function
    modifier updateVault(address account) {
        _updateFeesReward(account);
        _;
    }
    
    function _updateFeesReward(address account) internal {
        
        (uint256 collect0, uint256 collect1) = _collectFees();
        UserInfo storage user = userInfo[msg.sender];
        
        token0PerLiquidityStored = _token0PerLiquidity(collect0);
        token1PerLiquidityStored = _token1PerLiquidity(collect1);

        if (account != address(0)) {
            user.token0Rewards = _fee0Earned(account, token0PerLiquidityStored);
            user.token0PerLiquidityPaid = token0PerLiquidityStored;
            
            user.token1Rewards = _fee1Earned(account, token1PerLiquidityStored);
            user.token1PerLiquidityPaid = token1PerLiquidityStored;
        }
    }
    
    function _fee0Earned(address account, uint256 fee0PerLiquidity_) internal view returns (uint256) {
        UserInfo memory user = userInfo[account];
        return
            uint256(user.liquidity)
            .mul(fee0PerLiquidity_.sub(user.token0PerLiquidityPaid)) / uint256(1e18)
            .add(user.token0Rewards);
    }
    
    function _fee1Earned(address account, uint256 fee1PerLiquidity_) internal view returns (uint256) {
        UserInfo memory user = userInfo[account];
        return
            uint256(user.liquidity)
            .mul(fee1PerLiquidity_.sub(user.token1PerLiquidityPaid)) / uint256(1e18)
            .add(user.token1Rewards);
    }
    
    function _token0PerLiquidity(uint256 collected0) internal view returns (uint256) {
        uint _totalLiquidity = totalLiquidity;
        if (_totalLiquidity > 0) {
            return token0PerLiquidityStored.add(collected0.mul(1e18) / _totalLiquidity);
        }
        return token0PerLiquidityStored;
    }
    
    function _token1PerLiquidity(uint256 collected1) internal view returns (uint256) {
        uint _totalLiquidity = totalLiquidity;
        if (_totalLiquidity > 0) {
            return token1PerLiquidityStored.add(collected1.mul(1e18) / _totalLiquidity);
        }
        return token1PerLiquidityStored;
    }
    
    function getTwap() public view returns (int24) {
        uint32 _twapDuration = twapDuration;
        uint32[] memory secondsAgo = new uint32[](2);
        secondsAgo[0] = _twapDuration;
        secondsAgo[1] = 0;

        (int56[] memory tickCumulatives, ) = pool.observe(secondsAgo);
        return int24((tickCumulatives[1] - tickCumulatives[0]) / _twapDuration);
    }
    
}

interfaces/IUniswapV3Pool.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

import './pool/IUniswapV3PoolImmutables.sol';
import './pool/IUniswapV3PoolState.sol';
import './pool/IUniswapV3PoolDerivedState.sol';
import './pool/IUniswapV3PoolActions.sol';
import './pool/IUniswapV3PoolOwnerActions.sol';
import './pool/IUniswapV3PoolEvents.sol';

/// @title The interface for a Uniswap V3 Pool
/// @notice A Uniswap pool facilitates swapping and automated market making between any two assets that strictly conform
/// to the ERC20 specification
/// @dev The pool interface is broken up into many smaller pieces
interface IUniswapV3Pool is
    IUniswapV3PoolImmutables,
    IUniswapV3PoolState,
    IUniswapV3PoolDerivedState,
    IUniswapV3PoolActions,
    IUniswapV3PoolOwnerActions,
    IUniswapV3PoolEvents
{

}

interfaces/external/IWETH9.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.7.6;

import '../../token/IERC20.sol';

/// @title Interface for WETH9
interface IWETH9 is IERC20 {
    /// @notice Deposit ether to get wrapped ether
    function deposit() external payable;

    /// @notice Withdraw wrapped ether to get ether
    function withdraw(uint256) external;
}

interfaces/pool/IUniswapV3PoolActions.so.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

/// @title Permissionless pool actions
/// @notice Contains pool methods that can be called by anyone
interface IUniswapV3PoolActions {
    /// @notice Sets the initial price for the pool
    /// @dev Price is represented as a sqrt(amountToken1/amountToken0) Q64.96 value
    /// @param sqrtPriceX96 the initial sqrt price of the pool as a Q64.96
    function initialize(uint160 sqrtPriceX96) external;

    /// @notice Adds liquidity for the given recipient/tickLower/tickUpper position
    /// @dev The caller of this method receives a callback in the form of IUniswapV3MintCallback#uniswapV3MintCallback
    /// in which they must pay any token0 or token1 owed for the liquidity. The amount of token0/token1 due depends
    /// on tickLower, tickUpper, the amount of liquidity, and the current price.
    /// @param recipient The address for which the liquidity will be created
    /// @param tickLower The lower tick of the position in which to add liquidity
    /// @param tickUpper The upper tick of the position in which to add liquidity
    /// @param amount The amount of liquidity to mint
    /// @param data Any data that should be passed through to the callback
    /// @return amount0 The amount of token0 that was paid to mint the given amount of liquidity. Matches the value in the callback
    /// @return amount1 The amount of token1 that was paid to mint the given amount of liquidity. Matches the value in the callback
    function mint(
        address recipient,
        int24 tickLower,
        int24 tickUpper,
        uint128 amount,
        bytes calldata data
    ) external returns (uint256 amount0, uint256 amount1);

    /// @notice Collects tokens owed to a position
    /// @dev Does not recompute fees earned, which must be done either via mint or burn of any amount of liquidity.
    /// Collect must be called by the position owner. To withdraw only token0 or only token1, amount0Requested or
    /// amount1Requested may be set to zero. To withdraw all tokens owed, caller may pass any value greater than the
    /// actual tokens owed, e.g. type(uint128).max. Tokens owed may be from accumulated swap fees or burned liquidity.
    /// @param recipient The address which should receive the fees collected
    /// @param tickLower The lower tick of the position for which to collect fees
    /// @param tickUpper The upper tick of the position for which to collect fees
    /// @param amount0Requested How much token0 should be withdrawn from the fees owed
    /// @param amount1Requested How much token1 should be withdrawn from the fees owed
    /// @return amount0 The amount of fees collected in token0
    /// @return amount1 The amount of fees collected in token1
    function collect(
        address recipient,
        int24 tickLower,
        int24 tickUpper,
        uint128 amount0Requested,
        uint128 amount1Requested
    ) external returns (uint128 amount0, uint128 amount1);

    /// @notice Burn liquidity from the sender and account tokens owed for the liquidity to the position
    /// @dev Can be used to trigger a recalculation of fees owed to a position by calling with an amount of 0
    /// @dev Fees must be collected separately via a call to #collect
    /// @param tickLower The lower tick of the position for which to burn liquidity
    /// @param tickUpper The upper tick of the position for which to burn liquidity
    /// @param amount How much liquidity to burn
    /// @return amount0 The amount of token0 sent to the recipient
    /// @return amount1 The amount of token1 sent to the recipient
    function burn(
        int24 tickLower,
        int24 tickUpper,
        uint128 amount
    ) external returns (uint256 amount0, uint256 amount1);

    /// @notice Swap token0 for token1, or token1 for token0
    /// @dev The caller of this method receives a callback in the form of IUniswapV3SwapCallback#uniswapV3SwapCallback
    /// @param recipient The address to receive the output of the swap
    /// @param zeroForOne The direction of the swap, true for token0 to token1, false for token1 to token0
    /// @param amountSpecified The amount of the swap, which implicitly configures the swap as exact input (positive), or exact output (negative)
    /// @param sqrtPriceLimitX96 The Q64.96 sqrt price limit. If zero for one, the price cannot be less than this
    /// value after the swap. If one for zero, the price cannot be greater than this value after the swap
    /// @param data Any data to be passed through to the callback
    /// @return amount0 The delta of the balance of token0 of the pool, exact when negative, minimum when positive
    /// @return amount1 The delta of the balance of token1 of the pool, exact when negative, minimum when positive
    function swap(
        address recipient,
        bool zeroForOne,
        int256 amountSpecified,
        uint160 sqrtPriceLimitX96,
        bytes calldata data
    ) external returns (int256 amount0, int256 amount1);

    /// @notice Receive token0 and/or token1 and pay it back, plus a fee, in the callback
    /// @dev The caller of this method receives a callback in the form of IUniswapV3FlashCallback#uniswapV3FlashCallback
    /// @dev Can be used to donate underlying tokens pro-rata to currently in-range liquidity providers by calling
    /// with 0 amount{0,1} and sending the donation amount(s) from the callback
    /// @param recipient The address which will receive the token0 and token1 amounts
    /// @param amount0 The amount of token0 to send
    /// @param amount1 The amount of token1 to send
    /// @param data Any data to be passed through to the callback
    function flash(
        address recipient,
        uint256 amount0,
        uint256 amount1,
        bytes calldata data
    ) external;

    /// @notice Increase the maximum number of price and liquidity observations that this pool will store
    /// @dev This method is no-op if the pool already has an observationCardinalityNext greater than or equal to
    /// the input observationCardinalityNext.
    /// @param observationCardinalityNext The desired minimum number of observations for the pool to store
    function increaseObservationCardinalityNext(uint16 observationCardinalityNext) external;
}

