pragma solidity ^0.6.0;

import './SafeMath.sol';
import './IERC20.sol';

import './interface/IDistributor.sol';
import './interface/IRewardDistributionRecipient.sol';

contract InitialShareDistributor is IDistributor {
    using SafeMath for uint256;

    event Distributed(address pool, uint256 cashAmount);

    bool public once = true;

    IERC20 public share;
    IRewardDistributionRecipient public daiocLPPool;
    uint256 public daiocInitialBalance;
    IRewardDistributionRecipient public daiosLPPool;
    uint256 public daiosInitialBalance;

    constructor(
        IERC20 _share,
        IRewardDistributionRecipient _daiocLPPool,
        uint256 _daiocInitialBalance,
        IRewardDistributionRecipient _daiosLPPool,
        uint256 _daiosInitialBalance
    ) public {
        share = _share;
        daiocLPPool = _daiocLPPool;
        daiocInitialBalance = _daiocInitialBalance;
        daiosLPPool = _daiosLPPool;
        daiosInitialBalance = _daiosInitialBalance;
    }

    function distribute() public override {
        require(
            once,
            'InitialShareDistributor: you cannot run this function twice'
        );

        share.transfer(address(daiocLPPool), daiocInitialBalance);
        daiocLPPool.notifyRewardAmount(daiocInitialBalance);
        emit Distributed(address(daiocLPPool), daiocInitialBalance);

        share.transfer(address(daiosLPPool), daiosInitialBalance);
        daiosLPPool.notifyRewardAmount(daiosInitialBalance);
        emit Distributed(address(daiosLPPool), daiosInitialBalance);

        once = false;
    }
}

