contract COO_TeamRelease {
    using SafeMath for uint256;

    address owner;
    address public cooAddress;

    uint256 public lockedAmount;
    uint256 public startTime;
    uint256 public lockedTime = 2 * 365 days;

    constructor(address _cooAddress, uint256 amount) {
        owner = msg.sender;
        cooAddress = _cooAddress;
        startTime = block.timestamp;
        lockedAmount = amount;
    }

    function getUnclaimed() public view returns (uint256) {
        uint256 releasePreSec = lockedAmount.div(lockedTime);
        uint256 remainingTime = lockedTime.sub(block.timestamp.sub(startTime));
        uint256 remainingAmount = remainingTime.mul(releasePreSec);
        uint256 balance = IERC20(cooAddress).balanceOf(address(this));
        if(balance > remainingAmount) {
            return balance.sub(remainingAmount);
        }
        return 0;
    }

    function claim() public {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        uint256 amount = getUnclaimed();
        IERC20(cooAddress).transfer(msg.sender, amount);
    }

}
