/// @title UniswapLPOracleFactory
/// @author Claudia Richoux
////////////////////////////////////////////////////////////////////////////////////////////

contract SwapLPOracle is Ownable {
    using SafeMath for uint256;
    using WarpMath for uint256;


    address public usdc_add;
    address public weth_add;
    mapping(address => AggregatorV3Interface)
        public tokenaddrToChainlinkETHOracle;
    AggregatorV3Interface public chainlinkETHUSDCOracle;

    /**
    @notice constructor function is fired once during contract creation.
    @param usdcAdd is the address of the ERC20 USDC address
    @param wethAdd is the address of the ERC20 WETH address
    @param chainlink_eth_usdc_oracle is the address of the eth-usdc oracle
    **/
    constructor(
        address usdcAdd,
        address wethAdd,
        address chainlink_eth_usdc_oracle
    ) public {
        usdc_add = usdcAdd;
        weth_add = wethAdd;
        chainlinkETHUSDCOracle = AggregatorV3Interface(
            chainlink_eth_usdc_oracle
        );
    }

    function USDC() public view returns (address) {
        return usdc_add;
    }

    function OneUSDC() public view returns (uint256) {
        return OneToken(usdc_add);
    }

    function WETH() public view returns (address) {
        return weth_add;
    }

    function OneWeth() public view returns (uint256) {
        return OneToken(weth_add);
    }

    function OneToken(address token) public view returns (uint256) {
        ExtendedIERC20 ercToken = ExtendedIERC20(token);
        return uint256(10)**uint256(ercToken.decimals());
    }

    function addChainlinkETHOracle(address oracle, address token)
        public
        onlyOwner
    {
        require(
            token != usdc_add && token != weth_add,
            "Cannot add USDC or WETH tokens"
        );
        require(
            oracle != address(0) && token != address(0),
            "Oracle or token not valid"
        );

        tokenaddrToChainlinkETHOracle[token] = AggregatorV3Interface(oracle);
    }

    /**
    @notice getUnderlyingPrice allows for the price calculation and retrieval of a LP tokens price
    @param _lpToken is the address of the LP token  whos asset price is being retrieved
    @return returns the price of one LP token
    **/
    function getUnderlyingPrice(address _lpToken) public view returns (uint256) {
        IUniswapV2Pair lp_pair = IUniswapV2Pair(_lpToken);

        // get price of first token in usdc
        address token0 = lp_pair.token0();
        uint256 price0 = getPriceOfToken(token0, 10**18);

        // get price of second token in usdc
        address token1 = lp_pair.token1();
        uint256 price1 = getPriceOfToken(token1, 10**18);
        // get reserves
        (uint256 reserve0, uint256 reserve1, ) = lp_pair.getReserves();

        return
            _calculatePriceOfLP(
                ExtendedIERC20(_lpToken).totalSupply(),
                price0,
                price1,
                reserve0,
                reserve1
            );
    }

    function getLatestPrice(AggregatorV3Interface oracle)
        public
        view
        returns (uint256)
    {
        (, int256 price, , , ) = oracle.latestRoundData();
        assert(price > 0);
        return uint256(price);
    }

    function getPriceOfToken(address _token, uint256 _amount)
        public
        view
        returns (uint256)
    {
        if (_token == usdc_add) {
            return _amount;
        }

        uint256 eth_usdc_price = getLatestPrice(chainlinkETHUSDCOracle);
        if (_token == weth_add) {
            return (eth_usdc_price * _amount).div(1e20); //need to divide by 20 as eth has 18 decimals and chainlink oracle returns 8 decimals; result needs to have 6 decimals
        }
        uint256 token_eth_price = getLatestPrice(tokenaddrToChainlinkETHOracle[_token]);
        ExtendedIERC20 erc20Token = ExtendedIERC20(_token);
        uint256 tokenDecimals = erc20Token.decimals();
        return
            (token_eth_price * eth_usdc_price * _amount).div(
                10**(18 + 8 + tokenDecimals - 6)
            ); //token_eth_price = 18, eth_usdc_price = 8, _amount = tokenDecimals  - 6 (the precision we need for result)
    }

    /** Calculates price of LP tokens using ideal reserves: priceLP = 2 * sqrt(price0 * price1 * k) / LP_supply
    (More info at: https://cmichel.io/pricing-lp-tokens/)
     */
    function _calculatePriceOfLP(
        uint256 totalSupply,
        uint256 px0,
        uint256 px1,
        uint256 r0,
        uint256 r1
    ) public pure returns (uint256) {
        uint256 sqrtK = WarpMath.sqrt(r0.mul(r1)).wdiv(totalSupply); // in 2**112
        return
            sqrtK
                .mul(2)
                .mul(WarpMath.sqrt(px0))
                .div(2**56)
                .mul(WarpMath.sqrt(px1))
                .div(2**56);
    }
}
