contract ITO is Ownable {
   using SafeMath for uint256;
   
   uint256 constant public LIMIT = 150 ether;
   uint256 constant public SEED_AMOUNT = (22 * (10 ** 5)) * MAG;
   uint256 constant public RATE = 65 * (10 ** 13);
   uint256 constant public MAG = 10 ** 18;
   uint256 constant public window = 72 hours;
   bool public isSeeded;
   address public token;
   uint256 public start;
   mapping(address => uint256) public investments;

   function setStart() public onlyOwner {
       start = now;
   }  

   function set(address _token) public onlyOwner {
       token = _token;
       
   }
   
   function seed() external onlyOwner {
       require(!isSeeded);
       TransferHelper.safeTransferFrom(token, msg.sender, address(this), SEED_AMOUNT);
       isSeeded = true;
   }

   function withdrawTokens() public onlyOwner {
       require(now > (start + window));
       uint256 tokenBalance = IERC20(token).balanceOf(address(this));
       TransferHelper.safeTransfer(token, msg.sender, tokenBalance);
   }

   function withdraw() public onlyOwner {
       TransferHelper.safeTransferETH(owner(), address(this).balance);
   }
 
   function exchange() private {
       require(isSeeded);
       require(now <= (start + window));
       require(investments[msg.sender].add(msg.value) <= LIMIT);
       uint256 tokenBalance = IERC20(token).balanceOf(address(this));
       uint256 tradedAmount = (msg.value.mul(MAG)).div(RATE);
       require(tokenBalance >= tradedAmount, 'NO_MORE_TOKENS');
       TransferHelper.safeTransfer(token, msg.sender, tradedAmount);
       investments[msg.sender] = investments[msg.sender].add(msg.value);
   }
   
   receive() payable external {
       exchange();
   } 
    
}
