library SafeERC20 {
    using SafeMath for uint256;
    using Address for address;

    function safeTransfer(IERC20 token, address to, uint256 value) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.transfer.selector, to, value));
    }

    function safeTransferFrom(IERC20 token, address from, address to, uint256 value) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
    }

    /**
     * @dev Deprecated. This function has issues similar to the ones found in
     * {IERC20-approve}, and its usage is discouraged.
     *
     * Whenever possible, use {safeIncreaseAllowance} and
     * {safeDecreaseAllowance} instead.
     */
    function safeApprove(IERC20 token, address spender, uint256 value) internal {
        // safeApprove should only be called when setting an initial allowance,
        // or when resetting it to zero. To increase and decrease it, use
        // 'safeIncreaseAllowance' and 'safeDecreaseAllowance'
        // solhint-disable-next-line max-line-length
        require((value == 0) || (token.allowance(address(this), spender) == 0),
            "SafeERC20: approve from non-zero to non-zero allowance"
        );
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }

    function safeIncreaseAllowance(IERC20 token, address spender, uint256 value) internal {
        uint256 newAllowance = token.allowance(address(this), spender).add(value);
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }

    function safeDecreaseAllowance(IERC20 token, address spender, uint256 value) internal {
        uint256 newAllowance = token.allowance(address(this), spender).sub(value, "SafeERC20: decreased allowance below zero");
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }

    /**
     * @dev Imitates a Solidity high-level call (i.e. a regular function call to a contract), relaxing the requirement
     * on the return value: the return value is optional (but if data is returned, it must not be false).
     * @param token The token targeted by the call.
     * @param data The call data (encoded using abi.encode or one of its variants).
     */
    function _callOptionalReturn(IERC20 token, bytes memory data) private {
        // We need to perform a low level call here, to bypass Solidity's return data size checking mechanism, since
        // we're implementing it ourselves. We use {Address.functionCall} to perform this call, which verifies that
        // the target address contains contract code and also asserts for success in the low-level call.

        bytes memory returndata = address(token).functionCall(data, "SafeERC20: low-level call failed");
        if (returndata.length > 0) { // Return data is optional
            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation did not succeed");
        }
    }
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes memory) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}

contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contract PiptTrader is Ownable {
    using SafeERC20 for IERC20;
    using SafeERC20 for TokenInterface;

    PoolInterface public bPool;
    TokenInterface public piptToken;
    TokenInterface public weth;
    EthPiptSwapInterface public ethPiptSwap;



    constructor(PoolInterface bPool_, EthPiptSwapInterface ethPiptSwap_, TokenInterface piptToken_, TokenInterface weth_) {
        bPool = bPool_;
        ethPiptSwap = ethPiptSwap_;
        piptToken = piptToken_;
        weth = weth_;

    }

    receive() external payable {
        if (msg.sender != tx.origin) {
            return;
        }
        _refundLeftoverEth();

    }

    function mint_to_sell(uint256 _slippage) public payable onlyOwner {

        // Mint PIPT - In ETH Out PIPT
        EthPiptSwapInterface(ethPiptSwap).swapEthToPipt{ value: msg.value }(_slippage);

        // Sell PIPT - In PIPT Out WETH
        _bPoolTrade(piptToken, weth, 0, type(uint).max);
        //_tradePiptForEth(0, type(uint).max);

        // _refundLeftoverEth - Unwraps WETH and sends full ETH Balance
        _refundLeftoverEth();
    }

    function buy_to_redeem() public payable onlyOwner {
        _wrapEth();

        // Buy PIPT - In WETH Out PIPT
        _bPoolTrade(weth, piptToken, 0, type(uint).max);
        //_tradeEthForPipt(0, type(uint).max);

        // Redeem PIPT - 
        _redeem_pipt();

        // _refundLeftoverEth
        _refundLeftoverEth();
    }

    function _redeem_pipt() private {
        // Get Pipt balance
        uint piptBalance = piptToken.balanceOf(address(this));

        // Approve ethPiptSwap to spend pipt
        if (piptToken.allowance(address(this), address(ethPiptSwap)) < piptBalance) {
            piptToken.approve(address(ethPiptSwap), type(uint).max);    
        }

        // Redeem pipt
        EthPiptSwapInterface(ethPiptSwap).swapPiptToEth(piptBalance);

    }

    function _bPoolTrade(TokenInterface tokenIn, TokenInterface tokenOut, uint minAmountOut, uint maxPrice) private {
        // Get tokenIn balance
        uint tokenInBalance = tokenIn.balanceOf(address(this));

        // Approve spending of tokenIn by bPool if necessary
        if (tokenIn.allowance(address(this), address(bPool)) < tokenInBalance) {
            tokenIn.approve(address(bPool), type(uint).max);    
        }

        // Swap
        PoolInterface(bPool).swapExactAmountIn(
            address(tokenIn),            // Address tokenIn -> PIPT
            tokenInBalance,              // WETH balance after wrapping ETH
            address(tokenOut),        // Address tokenOut -> WETH
            minAmountOut,
            maxPrice
        );

    }

    // function _tradePiptForEth(uint minAmountOut, uint maxPrice) private {
    //     // Get Pipt balance
    //     uint piptBalance = piptToken.balanceOf(address(this));

    //     // Approve spending of PIPT by bPool if necessary
    //     if (piptToken.allowance(address(this), address(bPool)) < piptBalance) {
    //         piptToken.approve(address(bPool), type(uint).max);    
    //     }

    //     PoolInterface(bPool).swapExactAmountIn(
    //         address(piptToken),            // Address tokenIn -> PIPT
    //         piptBalance,              // WETH balance after wrapping ETH
    //         address(weth),        // Address tokenOut -> WETH
    //         minAmountOut,
    //         maxPrice
    //     );
    // }

    // function _tradeEthForPipt(uint minAmountOut, uint maxPrice) private {
    //     uint wethBalance = weth.balanceOf(address(this));

    //     if (weth.allowance(address(this), address(bPool)) < wethBalance) {
    //         weth.approve(address(bPool), type(uint).max);
    //     }

    //     PoolInterface(bPool).swapExactAmountIn(
    //         address(weth),            // Address tokenIn -> WETH
    //         wethBalance,              // WETH balance after wrapping ETH
    //         address(piptToken),        // Address tokenOut -> PIPT
    //         minAmountOut,
    //         maxPrice
    //     );
    // }

    function _wrapEth() private {
        weth.deposit{ value: msg.value }();

    }

    function _refundLeftoverEth() private {
        uint wethBalance = weth.balanceOf(address(this));

        if (wethBalance > 0) {
            weth.withdraw(wethBalance);
        }

        uint ethBalance = address(this).balance;

        address payable owner = payable(owner());

        if (ethBalance > 0) {
            (bool success,) = owner.call{ value: ethBalance }("");
            require(success, "ERR_ETH_FAILED");
        }
    }

    function withdrawEth() external onlyOwner {
        //msg.sender.transfer(address(this).balance);
        (bool success, ) = msg.sender.call{ value: address(this).balance } ("");
        require(success, "Transfer failed.");
    }

    function withdrawWEth() external onlyOwner {
        withdrawErc20(weth);
    }

    function withdrawPipt() external onlyOwner {
        withdrawErc20(piptToken);
    }   

    function withdrawErc20(IERC20 token) public onlyOwner {
        uint erc20Balance = token.balanceOf(address(this));
        token.transfer(msg.sender, erc20Balance);
    }
}
