contract TreasuryVester {
    using SafeMath for uint;

    IInv public inv;
    address public recipient;

    uint public vestingAmount;
    uint public vestingBegin;
    uint public vestingEnd;

    uint public lastUpdate;

    constructor(
        IInv inv_,
        uint vestingAmount_
    ) public {
        inv = inv_;
        recipient = msg.sender;

        vestingAmount = vestingAmount_;
        vestingBegin = block.timestamp;
        vestingEnd = vestingBegin + 730 days; // 2 years

        lastUpdate = block.timestamp;
        inv.delegate(msg.sender);
    }

    function delegate(address delegate_) public {
        require(msg.sender == recipient, 'TreasuryVester::delegate: unauthorized');
        inv.delegate(delegate_);
    }

    function setRecipient(address recipient_) public {
        require(msg.sender == recipient, 'TreasuryVester::setRecipient: unauthorized');
        recipient = recipient_;
    }

    function claimable() view public returns (uint amount) {
        if (block.timestamp >= vestingEnd) {
            amount = inv.balanceOf(address(this));
        } else {
            amount = vestingAmount.mul(block.timestamp - lastUpdate).div(vestingEnd - vestingBegin);
        }
    }

    function claim() public {
        uint amount;
        if (block.timestamp >= vestingEnd) {
            amount = inv.balanceOf(address(this));
        } else {
            amount = vestingAmount.mul(block.timestamp - lastUpdate).div(vestingEnd - vestingBegin);
            lastUpdate = block.timestamp;
        }
        inv.transfer(recipient, amount);
    }
}

interface IInv {
    function balanceOf(address account) external view returns (uint);
    function transfer(address dst, uint rawAmount) external returns (bool);
    function delegate(address delegatee) external;
}
