pragma solidity ^0.5.0;


contract CureCoin is IndividualLockableToken {
	using SafeMath for uint256;

	string public constant name     = "Cure Coin";
	string public constant symbol   = "CURE";
	uint8  public constant decimals = 18;

	uint256 public constant INITIAL_SUPPLY  = 5000000000 * (10 ** uint256(decimals));
	
	constructor()
		public
	{
		_mint(_msgSender(), INITIAL_SUPPLY);
	}

	function renounceOwnership()
		public
		onlyOwner
	{
		revert("The owner cannot release ownership.");
	}
	
	function transferOwnership(address newOwner)
		public
		onlyOwner
	{
		require(newOwner != address(0));
		require(newOwner != owner());

		addPauser(newOwner);
		addWhitelistAdmin(newOwner);
		super.transferOwnership(newOwner);
		renouncePauser();
		renounceWhitelistAdmin();
	}
}
