// File contracts/assets/Vault.sol

pragma solidity 0.6.12;


contract Vault is Ownable {

    IERC20 public immutable targetToken;
    address public spender;
    address public caller;

    event LockIn(address indexed user, uint256 amount);

    constructor(
        address _targetToken,
        address _spender,
        address _caller
    ) public {
        require(_targetToken != address(0), "Vault: zero address");
        require(_spender != address(0), "Vault: zero address");
        require(_caller != address(0), "Vault: zero address");
        targetToken = IERC20(_targetToken);
        spender = _spender;
        caller = _caller;
    }

    function setSpender(address _spender) public onlyOwner {
        require(_spender != address(0), "Vault: zero address");
        spender = _spender;
    }

     function setCaller(address _newAddress) public onlyOwner {
        require(_newAddress != address(0), "Vault: zero address");
        caller = _newAddress;
    }

    // need approve first
    function lockIn(uint256 amount) external {
        targetToken.transferFrom(msg.sender, address(this), amount);
        emit LockIn(msg.sender, amount);
    }
    // approve spender
    function approve(uint256 amount) external {
        require(msg.sender == caller, "Only from the caller");
        IERC20(targetToken).approve(spender, amount);
    }

}
