contract LORVestingVault {
	using SafeMath for uint256;
	using SafeMath for uint16;
	using SafeERC20 for IERC20;

	uint256 internal constant SECONDS_PER_DAY = 86400;

	struct Grant {
		uint256 startTime;
		uint256 amount;
		uint16 vestingDuration;
		uint16 vestingCliff;
		uint16 daysClaimed;
		uint256 totalClaimed;
		address recipient;
	}

	IERC20 public token;
	uint256 private _totalSupply;
	uint256 private _totalGranted;
	uint256 private _totalClaimed;

	address public denMultiSig;
	uint256 public totalVestingCount;

	mapping(uint256 => Grant) public tokenGrants;
	mapping(address => uint256[]) private activeGrants;

	event TokensAdded(uint256 amount);
	event GrantAdded(address indexed recipient, uint256 vestingId);
	event GrantTokensClaimed(address indexed recipient, uint256 amountClaimed);
	event GrantRemoved(address recipient, uint256 amountVested, uint256 amountNotVested);
	event ChangedMultisig(address multisig);

	constructor(IERC20 _token) public {
		require(address(_token) != address(0));
		denMultiSig = msg.sender;
		token = _token;
	}

	modifier onlyDenMultiSig {
		require(msg.sender == denMultiSig, "not owner");
		_;
	}

	modifier onlyGrantRecipient(uint256 _grantId) {
		Grant storage tokenGrant = tokenGrants[_grantId];
		require(msg.sender == tokenGrant.recipient, "not grant recipient");
		_;
	}

	modifier onlyValidAddress(address _recipient) {
		require(
			_recipient != address(0) && _recipient != address(this) && _recipient != address(token),
			"not valid recipient"
		);
		_;
	}

	function totalSupply() public view returns (uint256) {
		return _totalSupply;
	}

	function totalGranted() public view returns (uint256) {
		return _totalGranted;
	}

	function totalClaimed() public view returns (uint256) {
		return _totalClaimed;
	}

	function deposit(uint256 amount) public onlyDenMultiSig {
		_totalSupply = _totalSupply.add(amount);
		token.safeTransferFrom(msg.sender, address(this), amount);
		emit TokensAdded(amount);
	}

	function addTokenGrant(
		address _recipient,
		uint256 _startTime,
		uint256 _amount,
		uint16 _vestingDurationInDays,
		uint16 _vestingCliffInDays
	) external onlyDenMultiSig {
		require(_totalGranted.add(_amount) <= _totalSupply, "add more tokens");
		require(_vestingCliffInDays >= 30, "less than 30 days");
		require(_vestingCliffInDays <= 365, "more than 1 year");
		require(_vestingDurationInDays <= 10 * 365, "more than 10 years");
		require(_vestingDurationInDays >= _vestingCliffInDays, "Duration < Cliff");

		uint256 amountVestedPerDay = _amount.div(_vestingDurationInDays);
		require(amountVestedPerDay > 0, "vested per day must be more than 0");

		Grant memory grant = Grant({
			startTime: _startTime == 0 ? currentTime() : _startTime,
			amount: _amount,
			vestingDuration: _vestingDurationInDays,
			vestingCliff: _vestingCliffInDays,
			daysClaimed: 0,
			totalClaimed: 0,
			recipient: _recipient
		});

		// add the amount to total granted tokens
		_totalGranted = _totalGranted.add(_amount);

		tokenGrants[totalVestingCount] = grant;
		activeGrants[_recipient].push(totalVestingCount);
		emit GrantAdded(_recipient, totalVestingCount);
		totalVestingCount++;
	}

	function getActiveGrants(address _recipient) public view returns (uint256[] memory) {
		return activeGrants[_recipient];
	}

	/// @notice Calculate the vested and unclaimed months and tokens available for `_grantId` to claim
	/// Due to rounding errors once grant duration is reached, returns the entire grant amount remaining
	/// Returns (0, 0) if cliff has not been reached
	function calculateGrantClaim(uint256 _grantId) public view returns (uint16, uint256) {
		Grant storage tokenGrant = tokenGrants[_grantId];

		// For grants created with a future start date, that hasn't been reached, return 0, 0
		if (currentTime() < tokenGrant.startTime) {
			return (0, 0);
		}

		// Check cliff was reached
		uint256 elapsedTime = currentTime().sub(tokenGrant.startTime);
		uint256 elapsedDays = elapsedTime.div(SECONDS_PER_DAY);

		if (elapsedDays < tokenGrant.vestingCliff) {
			return (uint16(elapsedDays), 0);
		}

		// If over vesting duration, all tokens vested
		if (elapsedDays >= tokenGrant.vestingDuration) {
			uint256 remainingGrant = tokenGrant.amount.sub(tokenGrant.totalClaimed);
			return (tokenGrant.vestingDuration, remainingGrant);
		} else {
			uint16 daysVested = uint16(elapsedDays.sub(tokenGrant.daysClaimed));
			uint256 amountVestedPerDay = tokenGrant.amount.div(uint256(tokenGrant.vestingDuration));
			uint256 amountVested = uint256(daysVested.mul(amountVestedPerDay));
			return (daysVested, amountVested);
		}
	}

	/// @notice Allows a grant recipient to claim their vested tokens. Errors if no tokens have vested
	/// It is advised recipients check they are entitled to claim via `calculateGrantClaim` before calling this
	function claimVestedTokens(uint256 _grantId) external onlyGrantRecipient(_grantId) {
		uint16 daysVested;
		uint256 amountVested;
		(daysVested, amountVested) = calculateGrantClaim(_grantId);
		require(amountVested > 0, "vested amount is 0");
		require(_totalSupply.sub(amountVested) >= 0, "not enough tokens");

		Grant storage tokenGrant = tokenGrants[_grantId];
		tokenGrant.daysClaimed = uint16(tokenGrant.daysClaimed.add(daysVested));
		tokenGrant.totalClaimed = uint256(tokenGrant.totalClaimed.add(amountVested));

		// add the amount to total claimed tokens
		_totalClaimed = _totalClaimed.add(amountVested);

		// remove the amount from the total supply
		_totalSupply = _totalSupply.sub(amountVested);

		token.safeTransfer(tokenGrant.recipient, amountVested);
		emit GrantTokensClaimed(tokenGrant.recipient, amountVested);
	}

	/// @notice Terminate token grant transferring all vested tokens to the `_grantId`
	/// and returning all non-vested tokens to the Den MultiSig
	/// Secured to the Den MultiSig only
	/// @param _grantId grantId of the token grant recipient
	function removeTokenGrant(uint256 _grantId) external onlyDenMultiSig {
		Grant storage tokenGrant = tokenGrants[_grantId];
		address recipient = tokenGrant.recipient;
		uint16 daysVested;
		uint256 amountVested;
		(daysVested, amountVested) = calculateGrantClaim(_grantId);

		require(_totalSupply.sub(amountVested) >= 0, "not enough tokens");
		uint256 amountNotVested = (tokenGrant.amount.sub(tokenGrant.totalClaimed)).sub(amountVested);

		tokenGrant.startTime = 0;
		tokenGrant.amount = 0;
		tokenGrant.vestingDuration = 0;
		tokenGrant.vestingCliff = 0;
		tokenGrant.daysClaimed = 0;
		tokenGrant.totalClaimed = 0;
		tokenGrant.recipient = address(0);

		// add the vested amount to total claimed tokens
		_totalClaimed = _totalClaimed.add(amountVested);

		// remove the vested amount from the total supply
		_totalSupply = _totalSupply.sub(amountVested);

		// remove the non-vested amount from the total granted
		_totalGranted = _totalGranted.sub(amountNotVested);

		token.safeTransfer(recipient, amountVested);
		emit GrantRemoved(recipient, amountVested, amountNotVested);
	}

	function currentTime() private view returns (uint256) {
		return block.timestamp;
	}

	function tokensVestedPerDay(uint256 _grantId) public view returns (uint256) {
		Grant storage tokenGrant = tokenGrants[_grantId];
		return tokenGrant.amount.div(uint256(tokenGrant.vestingDuration));
	}

	function changeMultiSig(address _denMultiSig) external onlyDenMultiSig onlyValidAddress(_denMultiSig) {
		denMultiSig = _denMultiSig;
		emit ChangedMultisig(_denMultiSig);
	}
}
