// File contracts/Boost.sol

pragma solidity 0.7.3;





contract Boost is Context, Ownable, ReentrancyGuard {
  using SafeMath for uint256;

  IERC1155 private BRAINNFT;
  mapping(address => mapping(bool => mapping(uint256 => uint256))) private balance;
  mapping(address => mapping(bool => uint256)) public legandaryBalance;
  mapping(address => mapping(bool => uint256)) public exoticBalance;
  mapping(uint256 => uint256) private isAccepted;

  uint256 constant private LEGANDARY = 1;
  uint256 constant private EXOTIC = 2;

  event CardAdd(uint256 id);
  event UpdateBalance(address indexed account, uint256 bonus, bool farm);

  constructor(address _brainnft) {
    BRAINNFT = IERC1155(_brainnft);
  }

  function addCard(uint256 id, uint256 value) public onlyOwner {
    require(value == 1 || value == 2, "Must be 1 or 2");
    require(isAccepted[id] == 0, "Card already added");
    isAccepted[id] = value;
    emit CardAdd(id);
  }

  function balanceOf(address account, uint256 id, bool farm) public view returns (uint256) {
    return balance[account][farm][id];
  }

  function bonus(address account, bool farm) public view returns (uint256) {
    uint256 _bonus = exoticBalance[account][farm].add(legandaryBalance[account][farm]).mul(1000);
    if (_bonus <= 7000) {
      return _bonus;
    } else {
      return 7000;
    }
  }

  function deposit(uint256 id, uint256 amount, bool farm) public nonReentrant {
    require(isAccepted[id] > 0, "Card not whitelisted");
    balance[_msgSender()][farm][id] = balance[_msgSender()][farm][id].add(amount);
    if (isAccepted[id] == LEGANDARY) {
      require(legandaryBalance[_msgSender()][farm] < 5, "Max Legandary Stake");
      legandaryBalance[_msgSender()][farm] = legandaryBalance[_msgSender()][farm].add(amount);
    } else {
      require(exoticBalance[_msgSender()][farm] < 2, "Max Exotic Stake");
      exoticBalance[_msgSender()][farm] = exoticBalance[_msgSender()][farm].add(amount);
    }
    BRAINNFT.safeTransferFrom(_msgSender(), address(this), id, amount, "");
    emit UpdateBalance(_msgSender(), bonus(_msgSender(), farm), farm);
  }

  function withdraw(uint256 id, uint256 amount, bool farm) public nonReentrant  {
    require(balance[_msgSender()][farm][id] >= amount, "User does not have card");
    balance[_msgSender()][farm][id] = balance[_msgSender()][farm][id].sub(amount);
    if (isAccepted[id] == LEGANDARY) {
      legandaryBalance[_msgSender()][farm] = legandaryBalance[_msgSender()][farm].sub(amount);
    } else {
      exoticBalance[_msgSender()][farm] = exoticBalance[_msgSender()][farm].sub(amount);
    }
    BRAINNFT.safeTransferFrom(address(this), _msgSender(), id, amount, "");
    emit UpdateBalance(_msgSender(), bonus(_msgSender(), farm), farm);
  }
}
