contract WeakHandTax is Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router = IUniswapV2Router02(
        0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
    );
    address public _uniswapV2Pair;
    IERC20 public _token;
    uint256 public _latestTimeAdded;
    uint256 public constant MIN_BALANCE_TAX = 200e18;

    modifier isValidForAdder {
        require(
            _token.balanceOf(address(this)) > MIN_BALANCE_TAX,
            "Not enough MIN_BALANCE_TAX token to AddLiquidity"
        );
        _;
    }

    constructor(address uniswapV2Pair, address token) public {
        _uniswapV2Pair = uniswapV2Pair;
        _token = IERC20(token);
        _latestTimeAdded = block.timestamp;
    }

    function balanceOf() public view returns (uint256) {
        return _token.balanceOf(address(this));
    }

    receive() external payable {}

    function addTokenLiquidity() public isValidForAdder {
        uint256 _tokenBalance = _token.balanceOf(address(this));
        // split the contract balance into halves
        uint256 half = _tokenBalance.div(2);
        uint256 otherHalf = _tokenBalance.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half);

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        _latestTimeAdded = block.timestamp;
    }

    function approveUniswapRouter() external {
        _token.approve(
            address(uniswapV2Router),
            _token.balanceOf(address(this))
        );
    }

    function swapTokensForEth(uint256 tokenAmountForSwap) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(_token);
        path[1] = uniswapV2Router.WETH();

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmountForSwap,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(_token),
            tokenAmount,
            0, // slippage is unavoidable
