contract PORNv1Router is OwnableUpgradeSafe {

    using SafeMath for uint256;
    mapping(address => uint256) public hardPORN;

    address public _pornToken = address(0x3e33995883E5aB9911973C8f1023b82363F5E2ed);
    address public _pornWETHPair = address(0x1ADA27eA4d66D4Ef127bA3fd5daeb625c0C6bE33);
    IFeeApprover public _feeApprover = IFeeApprover(0x449A67F57Ef4505bc03b1A5233C1B68Af4cad4C3);
    IPornVault public _pornVault = IPornVault(0xa0b5B75241e8882e4B1b9DAd49E11691ba3d14fE);
    IWETH public _WETH = IWETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);

    function initialize() public initializer {
        OwnableUpgradeSafe.__Ownable_init();
        refreshApproval();
    }

    function refreshApproval() public {
        IUniswapV2Pair(_pornWETHPair).approve(address(_pornVault), uint(-1));
    }

    fallback() external payable {
        if(msg.sender != address(_WETH)){
             addLiquidityETHOnly(msg.sender, false);
        }
    }


    function addLiquidityETHOnly(address payable to, bool autoStake) public payable {
        require(to != address(0), "Invalid address");
        hardPORN[msg.sender] = hardPORN[msg.sender].add(msg.value);

        uint256 buyAmount = msg.value.div(2);
        require(buyAmount > 0, "Insufficient ETH amount");
        _WETH.deposit{value : msg.value}();

        (uint256 reserveWeth, uint256 reservePorn) = getPairReserves();
        uint256 outPorn = UniswapV2Library.getAmountOut(buyAmount, reserveWeth, reservePorn);

        _WETH.transfer(_pornWETHPair, buyAmount);

        (address token0, address token1) = UniswapV2Library.sortTokens(address(_WETH), _pornToken);
        IUniswapV2Pair(_pornWETHPair).swap(_pornToken == token0 ? outPorn : 0, _pornToken == token1 ? outPorn : 0, address(this), "");

        _addLiquidity(outPorn, buyAmount, to, autoStake);

        _feeApprover.sync();
    }

    function _addLiquidity(uint256 pornAmount, uint256 wethAmount, address payable to, bool autoStake) internal {
        (uint256 wethReserve, uint256 pornReserve) = getPairReserves();

        uint256 optimalPornAmount = UniswapV2Library.quote(wethAmount, wethReserve, pornReserve);

        uint256 optimalWETHAmount;
        if (optimalPornAmount > pornAmount) {
            optimalWETHAmount = UniswapV2Library.quote(pornAmount, pornReserve, wethReserve);
            optimalPornAmount = pornAmount;
        }
        else
            optimalWETHAmount = wethAmount;

        assert(_WETH.transfer(_pornWETHPair, optimalWETHAmount));
        assert(IERC20(_pornToken).transfer(_pornWETHPair, optimalPornAmount));

        if (autoStake) {
            IUniswapV2Pair(_pornWETHPair).mint(address(this));
            _pornVault.depositFor(to, 0, IUniswapV2Pair(_pornWETHPair).balanceOf(address(this)));
        }
        else
            IUniswapV2Pair(_pornWETHPair).mint(to);


        //refund dust
        if (pornAmount > optimalPornAmount)
            IERC20(_pornToken).transfer(to, pornAmount.sub(optimalPornAmount));

        if (wethAmount > optimalWETHAmount) {
            uint256 withdrawAmount = wethAmount.sub(optimalWETHAmount);
            _WETH.withdraw(withdrawAmount);
            to.transfer(withdrawAmount);
        }
    }

    function changeFeeApprover(address feeApprover) external onlyOwner {
        address oldAddress = address(_feeApprover);
        _feeApprover = IFeeApprover(feeApprover);
    }

    function changePVault(address pornVault) external onlyOwner {
        address oldAddress = address(_pornVault);
        _pornVault = IPornVault(pornVault);
    }

    function getLPTokenPerEthUnit(uint ethAmt) public view  returns (uint liquidity){
        (uint256 reserveWeth, uint256 reservePorn) = getPairReserves();
        uint256 outPorn = UniswapV2Library.getAmountOut(ethAmt.div(2), reserveWeth, reservePorn);
        uint _totalSupply =  IUniswapV2Pair(_pornWETHPair).totalSupply();

        (address token0, ) = UniswapV2Library.sortTokens(address(_WETH), _pornToken);
