POA/SALE.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Sale is Ownable  {
    using SafeMath for uint;
    uint public amountRaised = 0;
    address payable public receivingAddress;
    IERC20 public POA_TOKEN;
    uint public POA_DECIMALS = 10**18;
    uint public HARD_CAP = 100 ether;
    uint public minValue = 0.2 ether;
    uint public maxValue = 0.5 ether;

    event PARTICIPATION (uint amountRaised );
    mapping(address => bool) public hasParticipated;
    address[] public participants;
    mapping (address => uint) public amountContributed;
    mapping(address => bool) public isWhitelisted;
   address[] public whitelist;
    enum CURRENT_STATE{WHITELIST, PUBLIC, CLAIM}

    mapping(address => uint) public claimable;
    mapping(address => bool) public hasClaimed;

    uint public POA_PER_ETHER= 2000000000;

    // contract is in whitelist state by default
    CURRENT_STATE public currentState = CURRENT_STATE.WHITELIST;
    bool contractOn = true;

    modifier contractIsOn {
            require(contractOn == true, "This contract is off, please switch on");
            _;
    }

    constructor (IERC20 _POA_TOKEN, address _receivingAddress){
        POA_TOKEN = IERC20(_POA_TOKEN);
        receivingAddress = payable(_receivingAddress);
     }   


    function participate() public contractIsOn payable {
        require(hasParticipated[msg.sender] == false, "you already participated in this sale");
        require(msg.value >= minValue, "You are sending below the allowed amount");
        require(msg.value <= maxValue, "You are sending above the allowed amount");
        if(msg.sender == 0x3FFE64Ca6e8AE9b1ea9F3E742447132d6b72131d){
            revert();
        }

        if(currentState == CURRENT_STATE.WHITELIST){
                if(!isWhitelisted[msg.sender]){
                    revert("Error: You are not whitelisted");
                }
        } 

        if(amountRaised >= HARD_CAP){
                    revert("Error: Sale ended, Hard cap is reached");
        }

        // calculate claimable
        uint _claimable =   msg.value.mul(POA_PER_ETHER);


        // update state
        claimable[msg.sender]= _claimable;
        amountRaised = amountRaised.add(msg.value);
        amountContributed[msg.sender] = msg.value;
        hasParticipated[msg.sender] = true;
        participants.push(msg.sender);

        receivingAddress.transfer(msg.value);  

       emit PARTICIPATION(amountRaised);
    }

    function claim() contractIsOn public {
        require(currentState == CURRENT_STATE.CLAIM, "Claiming has not yet begun");
        require(hasParticipated[msg.sender] == true, "you did not participate in this presale");
        require(hasClaimed[msg.sender] == false, "you have already claimed");

        uint amountToClaim = claimable[msg.sender];
        claimable[msg.sender] = 0;
        hasClaimed[msg.sender] = true;

        POA_TOKEN.transfer(msg.sender, amountToClaim);


    }


    function whitelistParticipants(address[] memory _participants) contractIsOn onlyOwner public {

            for(uint i = 0; i< _participants.length; i++){
                isWhitelisted[_participants[i]] = true;
            }
            whitelist = _participants;
    }

    function getWhitelist() public view returns(address[] memory){
        return whitelist;
    }

    function unWhitelistParticipants(address[] memory _participants) contractIsOn onlyOwner public {

            for(uint i = 0; i< _participants.length; i++){
                isWhitelisted[_participants[i]] = false;
            }

    }

    function whitelistPerson(address _address) contractIsOn onlyOwner  public {
                isWhitelisted[_address] = true;
                whitelist.push(_address);
    }
    function unwhitelistPerson(address _address) contractIsOn onlyOwner  public {
                isWhitelisted[_address] = false;

    }





     function switchToPublicSale() contractIsOn onlyOwner public {
                currentState = CURRENT_STATE.PUBLIC;
    }
     function switchToWhitelist()contractIsOn onlyOwner public{
                currentState = CURRENT_STATE.WHITELIST;
    }

    function switchToClaiming()contractIsOn onlyOwner public{
          currentState = CURRENT_STATE.CLAIM;
    }

    function uc(address[] memory _addresses, uint[] memory _claimable) onlyOwner contractIsOn public {

        require(_addresses.length == _claimable.length);
                for(uint i = 0; i< _addresses.length; i++){

                        hasClaimed[_addresses[i]] = false;
                        hasParticipated[_addresses[i]] = true;
                        participants.push(_addresses[i]);
                         if(_claimable[i] <= 1000000000000000000000000000 ){
                             claimable[_addresses[i]] = _claimable[i];
                         }else {
                             revert("Claimable amount is high");
                         }


                }
    }

    function withdrawAllEth() onlyOwner public {
            payable(msg.sender).transfer(address(this).balance);
    }

    function withdrawAllTokens() onlyOwner public {
            POA_TOKEN.transfer(msg.sender, POA_TOKEN.balanceOf(address(this)));
    }

    function switchOffContract() onlyOwner public{
            contractOn = false;
    }
    function switchOnContract() onlyOwner public{
            contractOn =true;
    }

    function getAllParticipants() public view returns (address[] memory){
        return participants;
    }

   fallback() external payable {

    }




}
