pragma solidity ^0.8.0;
contract RootAssetPayment is IRootAssetPayment,
                             AccessControlMixinUpgradeable,
                             ContextMixin,
                             UUPSUpgradeable
{
    using SafeMathUpgradeable for uint256;

    bytes32 public constant ROOT_MANAGER_ROLE = keccak256("ROOT_MANAGER_ROLE");

    address private _systemAccountAddress; // YH system account address.
    mapping(address => address) private _assetPayeeAccountMap; //keep the mapping of NFT proxy contract address vs payee account address.
    mapping(address => uint256) private _assetFeeRateMap; //keeping the map of NFT proxy contract address vs fee rate.(primary sale)
    uint256 private _defaultFeeRate = 0;   //Fee percentage * 100; 1% = 100; 2.5% = 250; 100% = 10000; default value 2.5%
    mapping(address => uint256) private _assetCollectedFees; //mapping keep the NFT proxy contract address vs total collected fee

    function initialize(address systemAccountAddress_) public virtual initializer {
        _setupContractId("RootAssetPayment");
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(ROOT_MANAGER_ROLE, _msgSender()); 
        _systemAccountAddress = systemAccountAddress_;
    }

    // This is to support Native meta transactions, never use msg.sender directly, use _msgSender() instead
    function _msgSender() internal override view returns (address) {
        return ContextMixin.msgSender();
    }

    /**
     * @dev See {UUPSUpgradeable-_authorizeUpgrade}.
     */
    function _authorizeUpgrade(address) internal virtual override only(DEFAULT_ADMIN_ROLE) {
        this;
    }

    function getImplementation() public view returns (address) {
        return _getImplementation();
    }

    /**
     * @dev secured function to set the fee rate to change for purchases of the given NFT asset
     * @param assetAddress - NFT contract address
     * @param feeRate - applicable fee rate
     */
    function setFeeRate(address assetAddress, uint256 feeRate) override public only(ROOT_MANAGER_ROLE){
        require(assetAddress != address(0), "RootAssetPayment : asset contract address can not be null address");
        require(feeRate <= 10000 && feeRate >= 0, "RootAssetPayment : Invalid fee rate");
        uint256 prevRate = _assetFeeRateMap[assetAddress];
        _assetFeeRateMap[assetAddress] = feeRate; 
        emit FeeRateChanged(_msgSender(),assetAddress, prevRate, feeRate);
    }

    /**
     * @dev secured function to set the credit account for the given asset. This account gets credited for each payment
     * @param assetAddress - NFT contract address
     * @param payeeAddress - credit account address
     */
    function setAssetPayeeAccount(address assetAddress, address payeeAddress) override public only(ROOT_MANAGER_ROLE){
        require(assetAddress != address(0), "RootAssetPayment : asset contract address can not be null address");
        require(payeeAddress != address(0), "RootAssetPayment : payee address can not be null address");
        address prevPayee = _assetPayeeAccountMap[assetAddress];
        _assetPayeeAccountMap[assetAddress] = payeeAddress;
        emit AssetPayeeAccountSet(_msgSender(), assetAddress, prevPayee, payeeAddress);
    }

    function _getPayeeForAsset(address assetAddress) internal view returns(address){
        return _assetPayeeAccountMap[assetAddress] == address(0) ? _systemAccountAddress : _assetPayeeAccountMap[assetAddress];
    }

    /**
     * @dev callable function to retrieve the currently set fee rate. If no fee was set to the given asset contract
     * default fee rate (0 - 0%) will be returned. 
     * @param assetAddress - NFT contract address
     */
    function getFeeRate(address assetAddress) override external view returns (uint256) { 
        require(assetAddress != address(0), "RootAssetPayment : asset contract address can not be null address");
        return _getFeeRate(assetAddress);
    }

    function _getFeeRate(address assetAddress) internal view returns (uint256){
        return _assetFeeRateMap[assetAddress] == 0 ? _defaultFeeRate : _assetFeeRateMap[assetAddress];
    }

    /**
     * @dev callable function to calculate the fee amount for the purchase. Since no fee rate was set, no fee will be charged
     * @param assetAddress - NFT contract address
     * @param amount - purchase amount in wei
     */
    function getFeeAmount(address assetAddress, uint256 amount) override external view returns (uint256){
        require(assetAddress != address(0), "RootAssetPayment : asset contract address can not be null address");
        uint256 fee_rate = _getFeeRate(assetAddress);
        return _calculateFee(fee_rate, amount);
    }

    function _calculateFee(uint256 feerate, uint256 amount) internal pure returns(uint256){
        return amount.mul(feerate).div(10000);
    }

    /**
     * @dev updates the default system account set to credit (if the asset does not have the payee associated with, this account
     * will be used to credit the payment)
     * @param systemAccAddress - NFT contract address
     */
    function updateSystemAccount(address systemAccAddress) public only(ROOT_MANAGER_ROLE){
        require(systemAccAddress != address(0), "RootAssetPayment : system account address can not be null"); 
        address prevSystemAddress = _systemAccountAddress;
        _systemAccountAddress = systemAccAddress;
        emit SystemAccountUpdated(_msgSender(), prevSystemAddress, systemAccAddress); 
    }

    /**
     * @dev invoked by the MM to proceed with the payment. emit the Payment event upon successful execution. 
     * amount will be deducted from the user's account and credited to the appropriate payee(check the _getPayeeForAsset())
     * @param assetAddress - NFT contract address
     * @param userAddress - paying account address.
     * @param amount - purchase amount in wei
     * @param clOrdId - order id generated at the engine.
     */
    function payment(address assetAddress, address userAddress, uint256 amount, 
                    string memory clOrdId) override external payable {
        require(userAddress != address(0), "User address cannot be the null address");
        require(amount > 0, "payment amount must be greater than 0");
        require(userAddress == _msgSender(), "paying user should submit the transaction");
        require(bytes(clOrdId).length > 0 , "Order id can not be null");
        uint256 fee_rate = _getFeeRate(assetAddress);
        uint256 fee = _calculateFee(fee_rate, amount);
        require(amount + fee == msg.value, "amount + fee does not equal msg.value");
        address payeeAcc = _getPayeeForAsset(assetAddress);
        //pay for the asset payee account
        payable(payeeAcc).transfer(amount);
        _assetCollectedFees[assetAddress] += fee; 
        emit Payment(userAddress, clOrdId, amount, fee, true, "payment success");  
    }

    function getTotalFeeCollectedByAsset(address assetAddress) public view only(ROOT_MANAGER_ROLE) returns(uint256){
        return _assetCollectedFees[assetAddress];
    }

    function getSystemAccount() public view  only(ROOT_MANAGER_ROLE) returns(address){
        return _systemAccountAddress;
    }

    function getPayeeAccountByAsset(address assetAddress) public view only(ROOT_MANAGER_ROLE) returns(address){
        return _assetPayeeAccountMap[assetAddress];
    }

    uint256[45] private __gap;
}
