// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.6.12;

import {FlashLoanReceiverBase} from "FlashLoanReceiverBase.sol";
import {
    ILendingPool,
    ILendingPoolAddressesProvider,
    IERC20
} from "Interfaces.sol";
import {SafeMath, SafeERC20} from "Libraries.sol";

interface ICurveYSwap {
    function exchange_underlying(
        int128 from,
        int128 to,
        uint256 _from_amount,
        uint256 _min_to_amount
    ) external;
}

interface ICurveDeposit {
    function add_liquidity(
        uint256[4] calldata uamounts,
        uint256 min_mint_amount
    ) external;

    function remove_liquidity_one_coin(
        uint256 _token_amount,
        int128 i,
        uint256 min_uamount
    ) external;
}

interface IYVaultZap {
    function mint(uint256[4] calldata inAmounts, uint256 minDusdAmount)
        external
        returns (uint256);

    function redeemInSingleCoin(
        uint256 dusdAmount,
        uint256 i,
        uint256 minOut
    ) external;
}

interface UniswapV2Router {
    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function WETH() external pure returns (address);
}

contract myFlashloan_aave is FlashLoanReceiverBase {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint8 runTimes = 1;
    bool bProved = false;
    uint8 test_type = 0;

    //Mainnet
    //DAI USDC USDT TUSD
    address[4] addrCoin = [
        0x6B175474E89094C44Da98b954EedeAC495271d0F,
        0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48,
        0xdAC17F958D2ee523a2206206994597C13D831ec7,
        0x0000000000085d4780B73119b644AE5ecd22b376
    ];

    IERC20 yCrv = IERC20(0xdF5e0e81Dff6FAF3A7e52BA697820c5e32D806A8);
    IERC20 dusd = IERC20(0x5BC25f649fc4e26069dDF4cF4010F9f706c23831);
    ICurveDeposit yDeposit =
        ICurveDeposit(0xbBC81d23Ea2c3ec7e56D39296F0cbB648873a5d3);
    ICurveYSwap ySwap = ICurveYSwap(0x45F783CCE6B7FF23B2ab2D70e416cdb7D6055f51);
    IYVaultZap yvzap = IYVaultZap(0xed373A23F7e69c2d7dBD0eE4e744a10Ce4D2602D);
    UniswapV2Router univ2r =
        UniswapV2Router(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    modifier onlyOwner {
        assert(owner == msg.sender);
        _;
    }

    constructor(ILendingPoolAddressesProvider _addressProvider)
        public
        FlashLoanReceiverBase(_addressProvider)
    {}

    receive() external payable {}

    function kill() public onlyOwner() {
        selfdestruct(owner);
    }

    /**
        This function is called after your contract has received the flash loaned amount
     */
    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        // Approve the LendingPool contract allowance to *pull* the owed amount
        uint256[] memory amnOwns = new uint256[](2);
        amnOwns[0] = amounts[0].add(premiums[0]);
        amnOwns[1] = amounts[1].add(premiums[1]);
        IERC20(assets[0]).safeApprove(address(LENDING_POOL), 0);
        IERC20(assets[0]).safeApprove(address(LENDING_POOL), amnOwns[0]);
        IERC20(assets[1]).safeApprove(address(LENDING_POOL), 0);
        IERC20(assets[1]).safeApprove(address(LENDING_POOL), amnOwns[1]);

        trade_dusd(assets, amounts, amnOwns);

        return true;
    }

    function FlashLoanCall(
        address[] calldata _asset,
        uint256[] calldata _amount,
        uint8 _test_type,
        uint8 _runTimes
    ) public onlyOwner() {
        test_type = _test_type;
        runTimes = _runTimes;

        address receiverAddress = address(this);

        address[] memory assets = new address[](2);
        assets[0] = _asset[0];
        assets[1] = _asset[1];

        uint256[] memory amounts = new uint256[](2);
        amounts[0] = _amount[0];
        amounts[1] = _amount[1];

        // 0 = no debt, 1 = stable, 2 = variable
        uint256[] memory modes = new uint256[](2);
        modes[0] = 0;
        modes[1] = 0;

        address onBehalfOf = address(this);
        bytes memory params = "";
        uint16 referralCode = 0;

        LENDING_POOL.flashLoan(
            receiverAddress,
            assets,
            amounts,
            modes,
            onBehalfOf,
            params,
            referralCode
        );
    }

    //borrow usdt&usdc,trade in defidollar
    function trade_dusd(
        address[] memory _assets,
        uint256[] memory _amounts,
        uint256[] memory _amnOwns
    ) internal {
        uint8 t1 = tokenPos(_assets[0]);
        uint8 t2 = tokenPos(_assets[1]);
        IERC20 tk1 = IERC20(_assets[0]);
        IERC20 tk2 = IERC20(_assets[1]);

        if (bProved == false) {
            tk1.safeApprove(address(yvzap), uint256(-1));
            tk2.safeApprove(address(yvzap), uint256(-1));
            dusd.safeApprove(address(yvzap), uint256(-1));
            tk1.safeApprove(address(yDeposit), uint256(-1));
            tk2.safeApprove(address(yDeposit), uint256(-1));
            yCrv.safeApprove(address(yDeposit), uint256(-1));
            tk1.safeApprove(address(ySwap), uint256(-1));
            tk2.safeApprove(address(ySwap), uint256(-1));
            yCrv.safeApprove(address(ySwap), uint256(-1));

            bProved = true;
        }

        if (test_type == 1) {
            for (uint8 i = 0; i < runTimes; i++) {
                //deposit tk2 into VaultZap to mint DUSD,tk2->dusd
                yvzap.mint(tokenArr(t2, _amounts[1]), 0);
                //redeem from dusd,dusd->tk1
                yvzap.redeemInSingleCoin(dusd.balanceOf(address(this)), t1, 0);

                //deposit tk1 into curve.fi y pool,tk1->tk2
                yDeposit.add_liquidity(tokenArr(t1, _amounts[0]), 0);
                yDeposit.remove_liquidity_one_coin(
                    yCrv.balanceOf(address(this)),
                    t2,
                    0
                );

                if (tk2.balanceOf(address(this)) <= _amnOwns[1]) break;

                uint256 tk2_left = tk2.balanceOf(address(this)) - _amnOwns[1];
                ySwap.exchange_underlying(t2, t1, tk2_left, 0);

                if (tk1.balanceOf(address(this)) < _amounts[0]) break;
            }

            coinToETH(_assets[0], _amnOwns[0]);
        }
    }

    //token->eth
    function swaptoETH(address addrtk, uint256 amn) internal {
        IERC20(addrtk).safeApprove(address(univ2r), 0);
        IERC20(addrtk).safeApprove(address(univ2r), amn);
        address[] memory uniData = new address[](2);
        uniData[0] = addrtk;
        uniData[1] = univ2r.WETH();
        univ2r.swapExactTokensForETH(
            amn,
            1,
            uniData,
            address(this),
            uint256(block.timestamp).add(3600)
        );
    }

    function coinToETH(address _asset, uint256 _amnOwn) internal {
        if (IERC20(_asset).balanceOf(address(this)) > _amnOwn) {
            swaptoETH(
                _asset,
                IERC20(_asset).balanceOf(address(this)).sub(_amnOwn)
            );

            owner.transfer(address(this).balance);
        }
    }

    function tokenArr(uint8 _pos, uint256 _amn)
        public
        pure
        returns (uint256[4] memory arr)
    {
        uint256[4] memory _arr =
            [uint256(0), uint256(0), uint256(0), uint256(0)];

        for (uint8 i = 0; i < 4; i++) {
            if (i == _pos) {
                _arr[i] = _amn;
                break;
            }
        }

        return _arr;
    }

    function tokenPos(address _asset) public view returns (uint8 pos) {
        uint8 _pos;
        for (uint8 i = 0; i < 4; i++) {
            if (addrCoin[i] == _asset) {
                _pos = i;
                break;
            }
        }

        return _pos;
    }

    function withdrawToken(address _asset) public onlyOwner() {
        if (_asset == address(0)) owner.transfer(address(this).balance);
        else
            IERC20(_asset).transfer(
                msg.sender,
                IERC20(_asset).balanceOf(address(this))
            );
    }

    function checkToken(address _asset) public view returns (uint256) {
        if (_asset == address(0)) return address(this).balance;
        else return IERC20(_asset).balanceOf(address(this));
    }
}
