  function _cost(uint256 _supply)
        private 
        pure
        returns (uint256)
    {
        uint256 price;
        if (_supply >= 0 && _supply < 500) {
            price = 0 ether;
        }
        else if (_supply >= 500 && _supply < 2000) {
            price = 0.02 ether;
        }
        else if (_supply >= 2000 && _supply < 5000) {
            price = 0.04 ether;
        }
        else if (_supply >= 5000 && _supply < 8000) {
            price = 0.08 ether;
        }
        else if (_supply >= 8000 && _supply < 9000) {
            price = 0.16 ether;
        }
        else if (_supply >= 9000 && _supply < 9500) {
            price = 0.32 ether;
        }
        else if (_supply >= 9500 && _supply < 9900) {
            price = 0.64 ether;
        }
        else if (_supply >= 9900 && _supply < 9999) {
            price = 1.28 ether;
        }
        else {
            price = 100 ether;
        }

        return price;
    }
  
  function _getTokenToBeMinted(uint256 _totalMintedTokens)
        private
        returns (uint256)
    {
        uint256 maxIndex = maxSupply - _totalMintedTokens;
        uint256 random = _getRandomNumber(maxIndex, _totalMintedTokens);

        uint256 tokenId = tokenMatrix[random];
        if (tokenMatrix[random] == 0) {
            tokenId = random;
        }

        tokenMatrix[maxIndex - 1] == 0
            ? tokenMatrix[random] = maxIndex - 1
            : tokenMatrix[random] = tokenMatrix[maxIndex - 1];

        return tokenId;
    }

    /**
     * @dev Generates a pseudo-random number.
     */
    function _getRandomNumber(uint256 _upper, uint256 _totalMintedTokens)
        private
        view
        returns (uint256)
    {
        uint256 random = uint256(
            uint256(
                keccak256(
                    abi.encodePacked(
                        _totalMintedTokens,
                        blockhash(block.number - 1),
                        block.coinbase,
                        block.difficulty,
                        msg.sender
                    )
                )
            )
        );

        return random % _upper;
    }
  
