    function _computeNominalCR(uint _coll, uint _debt, uint _collDecimalAdjustment) internal pure returns (uint) {
        if (_debt > 0) {
            return _coll.mul(_collDecimalAdjustment).mul(NICR_PRECISION).div(_debt);
